// Parameters
const int MAX_MARCHING_STEPS = 128;
const float MIN_DIST = 0.0;
const float MAX_DIST = 128.0;
const float PRECISION = 0.001;
const float EPSILON = 0.005;

const float PI = 3.14159265359;

const vec3 BACKGROUND_COLOR = vec3(0.6, 0.6, 0.835);
const vec3 TORUS_COLOR = vec3(1.0, 1.0, 1.0);

const vec3 AMBIENT = vec3(0.5, 0.5, 0.5);

const vec3 CAM_LOOK_AT = vec3(0, 0, 0);
const vec3 LIGHT_POS = vec3(2., 5, -1.0);


vec3 textColor(vec3 p, vec3 normal) {
    float b = dot(normal, vec3(0.0, 0.0, 1.0));
    vec2 q = vec2(0.5 - 0.5 * atan(normal.z, normal.x) / PI, -acos(p.y) / PI);
    return texture(iChannel0, vec2(iTime / 5.0, 0.0) + q).xyz;
}

struct Surface {
    float id;
    float sd; // signed distance value
    vec3 col; // color
};

// https://iquilezles.org/articles/distfunctions/
Surface sdSphere( vec3 p, float r, vec3 offset ) {
  p = p - offset;
  vec3 n = normalize(p);
  vec3 color = textColor(p, n);

  // displacement mapping based on the texture
  // not the best, but the simplest
  p = p - 0.05*(0.21*color.r + 0.71*color.g + 0.07*color.b);

  float d = length(p)-r;
  return Surface(1.0, d, color);
}

Surface sdTorus( vec3 p, vec2 t, vec3 offset, vec3 col ) {
  p = p - offset;
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  float d = length(q)-t.y;
  return Surface(2.0, d, col);
}

Surface sdFloor(vec3 p, vec3 col) {
  float d = p.y + 1.;
  return Surface(0.0, d, col);
}

Surface unionSDF(Surface obj1, Surface obj2) {
    if (obj1.sd < obj2.sd) return obj1;
    return obj2;
}

Surface sdScene(vec3 p) {
  vec3 floorColor = vec3(0.5, 1. + 0.7*mod(floor(p.x) + floor(p.z), 2.0), 0.5);
  Surface flooor = sdFloor(p, floorColor);

  Surface sphere = sdSphere(p, 1., vec3(-1.5*cos(iTime), 0, 1.5*sin(iTime)));
  Surface torus = sdTorus(p, vec2(1.0, 0.25), vec3(1.5*cos(iTime), 0, -1.5*sin(iTime)), TORUS_COLOR);
  //Surface sphere = sdSphere(p, 1., vec3(-1.5, 0, 0));
  //Surface torus = sdTorus(p, vec2(1.0, 0.25), vec3(1.5, 0, 0), BALL_COLOR);

  Surface co = sphere;
  co = unionSDF(co, torus);
  co = unionSDF(co, flooor);
  return co;
}

Surface rayMarch(vec3 ro, vec3 rd) {
  float depth = MIN_DIST;
  Surface co; // closest object

  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    co = sdScene(p);
    depth += co.sd;
    if (co.sd < PRECISION || depth > MAX_DIST) break;
  }

  co.sd = depth;

  return co;
}

// https://iquilezles.org/articles/rmshadows/
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float w )
{
    float res = 1.0;
    float ph = 1e20;
    float t = mint;
    for( int i=0; i<256 && t<maxt; i++ )
    {
        float h = sdScene(ro + rd*t).sd;
        if( h<0.001 )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, d/(w*max(0.0,t-y)) );
        ph = h;
        t += h;
    }
    return res;
}

// https://iquilezles.org/articles/normalsSDF/
vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(1, -1) * EPSILON;
    return normalize(
      e.xyy * sdScene(p + e.xyy).sd +
      e.yyx * sdScene(p + e.yyx).sd +
      e.yxy * sdScene(p + e.yxy).sd +
      e.xxx * sdScene(p + e.xxx).sd);
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 forward = normalize(cameraPos - lookAtPoint); // camera direction
	vec3 right = normalize(cross(vec3(0, 1, 0), forward)); // camera right
	vec3 up = normalize(cross(forward, right)); // camera up
	return mat3(right, up, forward);
}


float angle(vec3 v1, vec3 v2) {
    return clamp(dot(v1, v2), 0.0, 1.0);
}

// Phong reflection model
float lightIntensity(vec3 p, vec3 norm, vec3 camPos, vec3 lightPos) {
    float kd = 0.9;
    float ks = 0.7;
    float id = 0.7;
    float is = 0.5;
    float a = 4.0;

    vec3 lightDir = normalize(lightPos - p);
    vec3 camDir = normalize(camPos - p);

    vec3 reflectDir = 2.0 * angle(lightDir, norm) * norm - lightDir;
    float diffuseLight = kd * angle(lightDir, norm) * id;
    float specularLight = ks * pow(angle(reflectDir, camDir), a) * is;

    float color = (diffuseLight + specularLight);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec3 camPos = vec3(4.0*sin(iTime/5.0), 1.5, 4.0*cos(iTime/5.0));

  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

  vec3 col;

  mat3 camMatrix = camera(camPos, CAM_LOOK_AT);
  vec3 rd = camMatrix * normalize(vec3(uv, -1.)); // ray direction

  Surface co = rayMarch(camPos, rd); // closest object

  float dist = co.sd;

  if (co.sd > MAX_DIST) {
    col = BACKGROUND_COLOR; // ray didn't hit anything
  } else {
    vec3 p = camPos + rd * co.sd; // point on object or floor we discovered from ray marching
    vec3 normal = calcNormal(p);

    col = co.col * (0.5*lightIntensity(p, normal, camPos, LIGHT_POS) + AMBIENT);

    vec3 lightDir = normalize(LIGHT_POS - p);
    vec3 newRayOrigin = p + normal * PRECISION * 2.;

    // if torus, we have a reflective surface:
    // cast a secondary ray
    if (co.id == 2.0) {
        rd = reflect(rd, normal);
        co = rayMarch(newRayOrigin, rd);
        if (co.sd < MAX_DIST) {
          col = 0.5*col + 0.5*co.col;
        }
        else
          col = 0.5*col + 0.5*BACKGROUND_COLOR;
    }

    // cast shadow ray to the light source
    // https://iquilezles.org/articles/rmshadows/
    float shadow = softshadow(newRayOrigin, lightDir, 0.01, 3.0, 0.2);
    shadow = clamp(0.0, 1.0, shadow + 0.3);
    col = shadow*col;
  }

  // apply fog effect
  // https://iquilezles.org/articles/fog/
  float fogAmount = 1.0 - exp( -dist*0.05 );
  vec3  fogColor  = vec3(0.5,0.6,0.7);
  col = mix( col, fogColor, fogAmount );

  // Output to screen
  fragColor = vec4(col, 1.0);
}

// Parameters
const int MAX_MARCHING_STEPS = 64;
const int MAX_MARCHING_STEPS_SHADOW = 32;
const float MIN_DIST = 0.0;
const float MAX_DIST = 12.0;
const float PRECISION = 0.01;
const float EPSILON = 0.005;

const float BACKGROUND_COLOR = 0.835;
const float BALL_COLOR = 0.7;

const vec3 CAM_LOOK_AT = vec3(0, 0, 0);
const vec3 LIGHT_POS = vec3(2., 5, -1.0);

// ordered dithering 8x8 matrix
float ditherMatrix[64] = float[64]( 0., 48., 12., 60.,  3., 51., 15., 63.,
                                   32., 16., 44., 28., 35., 19., 47., 31.,
                                    8., 56.,  4., 52., 11., 59.,  7., 55.,
                                   40., 24., 36., 20., 43., 27., 39., 23.,
                                    2., 50., 14., 62.,  1., 49., 13., 61.,
                                   34., 18., 46., 30., 33., 17., 45., 29.,
                                   10., 58.,  6., 54.,  9., 57.,  5., 53.,
                                   42., 26., 38., 22., 41., 25., 37., 21.);

struct Surface {
    float sd; // signed distance value
    float col; // color
};

// https://iquilezles.org/articles/distfunctions/
Surface sdSphere( vec3 p, float r, vec3 offset, float col ) {
  p = p - offset;
  float d = length(p)-r;
  return Surface(d, col);
}

Surface sdTorus( vec3 p, vec2 t, vec3 offset, float col ) {
  p = p - offset;
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  float d = length(q)-t.y;
  return Surface(d, col);
}

Surface sdFloor(vec3 p, float col) {
  float d = p.y + 1.;
  return Surface(d, col);
}

Surface unionSDF(Surface obj1, Surface obj2) {
    if (obj1.sd < obj2.sd) return obj1;
    return obj2;
}

Surface sdScene(vec3 p) {
  float floorColor = 1. + 0.7*mod(floor(p.x) + floor(p.z), 2.0);
  Surface flooor = sdFloor(p, floorColor);

  Surface sphere = sdSphere(p, 1., vec3(-1.5*cos(iTime), 0, 1.5*sin(iTime)), BALL_COLOR);
  Surface torus = sdTorus(p, vec2(1.0, 0.25), vec3(1.5*cos(iTime), 0, -1.5*sin(iTime)), BALL_COLOR);
  //Surface sphere = sdSphere(p, 1., vec3(-1.5, 0, 0), BALL_COLOR);
  //Surface torus = sdTorus(p, vec2(1.0, 0.25), vec3(1.5, 0, 0), BALL_COLOR);

  Surface co = sphere;
  co = unionSDF(co, torus);
  co = unionSDF(co, flooor);
  return co;
}

Surface rayMarch(vec3 ro, vec3 rd, int maxMarchingSteps) {
  float depth = MIN_DIST;
  Surface co; // closest object

  for (int i = 0; i < maxMarchingSteps; i++) {
    vec3 p = ro + depth * rd;
    co = sdScene(p);
    depth += co.sd;
    if (co.sd < PRECISION || depth > MAX_DIST) break;
  }

  co.sd = depth;

  return co;
}

// https://iquilezles.org/articles/normalsSDF/
vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(1, -1) * EPSILON;
    return normalize(
      e.xyy * sdScene(p + e.xyy).sd +
      e.yyx * sdScene(p + e.yyx).sd +
      e.yxy * sdScene(p + e.yxy).sd +
      e.xxx * sdScene(p + e.xxx).sd);
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 forward = normalize(cameraPos - lookAtPoint); // camera direction
	vec3 right = normalize(cross(vec3(0, 1, 0), forward)); // camera right
	vec3 up = normalize(cross(forward, right)); // camera up
	return mat3(right, up, forward);
}


float angle(vec3 v1, vec3 v2) {
    return clamp(dot(v1, v2), 0.0, 1.0);
}

// Phong reflection model
float lightIntensity(vec3 p, vec3 norm, vec3 camPos, vec3 lightPos) {
    float kd = 0.9;
    float ks = 0.7;
    float id = 0.7;
    float is = 0.5;
    float a = 4.0;

    vec3 lightDir = normalize(lightPos - p);
    vec3 camDir = normalize(camPos - p);

    vec3 reflectDir = 2.0 * angle(lightDir, norm) * norm - lightDir;
    float diffuseLight = kd * angle(lightDir, norm) * id;
    float specularLight = ks * pow(angle(reflectDir, camDir), a) * is;

    float color = (diffuseLight + specularLight);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec3 camPos = vec3(4.0*sin(iTime/5.0), 1.5, 4.0*cos(iTime/5.0));

  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

  float col = 0.0;

  mat3 camMatrix = camera(camPos, CAM_LOOK_AT);
  vec3 rd = camMatrix * normalize(vec3(uv, -1.)); // ray direction

  Surface co = rayMarch(camPos, rd, MAX_MARCHING_STEPS); // closest object

  if (co.sd > MAX_DIST) {
    col = BACKGROUND_COLOR; // ray didn't hit anything
  } else {
    vec3 p = camPos + rd * co.sd; // point on object or floor we discovered from ray marching
    vec3 normal = calcNormal(p);

    col = co.col * lightIntensity(p, normal, camPos, LIGHT_POS) + 0.2*BACKGROUND_COLOR;

    vec3 lightDir = normalize(LIGHT_POS - p);
    vec3 newRayOrigin = p + normal * PRECISION * 2.;
    // cast shadow ray to the light source
    float shadowRayLength = rayMarch(newRayOrigin, lightDir, MAX_MARCHING_STEPS_SHADOW).sd;
    if (shadowRayLength < length(LIGHT_POS - newRayOrigin))
        col *= 0.25;
  }
  col = clamp(col, 0.0, 1.0);

  // dithering
  if (uv.x > -cos(iTime/2.0)) {
    col = col + ditherMatrix[8 * int(mod(fragCoord.x, 8.0)) + int(mod(fragCoord.y, 8.0))] / 64.0 - 0.5;
    col = col < 0.5 ? 0.0 : 1.0;
  }

  // Output to screen
  fragColor = vec4(col, col, col, 1.0);
}

