vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

mat2 rot(float t){
    return mat2(
        cos(t), sin(t),
        -sin(t), cos(t)
    );
}

