float chebyshevDistance(vec2 a, vec2 b) {
    return max(abs(a.x - b.x), abs(a.y - b.y));
}

vec2 unity_voronoi_noise_randomVector(vec2 UV, float offset)
{
    mat2 m = mat2(15.27, 47.63, 99.41, 89.98);
    UV = fract(sin(UV*m) * 46839.32);
    return vec2(sin(UV.y + offset) * 0.5 + 0.5, cos(UV.x * offset) * 0.5 + 0.5);
}
float voronoi(vec2 p,float density,float angle)
{ 
    float AngleOffset = angle;
    float CellDensity = density;

    vec2 g = floor(p * CellDensity);
    vec2 f = fract(p * CellDensity);

    vec3 res = vec3(8.0, 0.0, 0.0);

    for(int y = -1; y <= 1; y++)
    {
        for(int x = -1; x <= 1; x++)
        {
            vec2 lattice = vec2(x, y);
            vec2 offset = unity_voronoi_noise_randomVector(lattice + g, AngleOffset);
            float d = chebyshevDistance(lattice + offset, f);
            if(d < res.x)
            {
                res = vec3(d, offset.x, offset.y);
            }
        }
    }

 return res.x;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 UV = fragCoord / iResolution.xy;
    float AngleOffset = 5.0*iTime+10.0;
    float CellDensity = 2.0*iTime+10.0;

    float f = voronoi(UV,AngleOffset,CellDensity);

    // Visualize the f vector
    fragColor = vec4(f, f,f, 1.0);


}

#define S(a, b, t) smoothstep(a,b,t)

float rand(float co1, float co2){
    return fract(sin(dot(vec2(co1,co2), vec2(12.9898, 78.233))) * 43758.5453);
}

//float rr(float r){
