vec4 background(vec3 p,vec3 d) {
    float x = iPlane(p-vec3(0,-1,0),d,vec3(0,1,0));
    vec3 q = p+d*x;

    if (x < 0.) {
        float x = iPlane(p-vec3(0,1,0),d,vec3(0,-1,0));
        vec3 q = p+d*x;
        vec2 u = abs(sin(vec2(5,10)*q.xz));
        float s = min(u.x,u.y);
        return vec4(2)*smoothstep(0.1,0.2,s)*exp(-.2*x);
    }

    vec2 u = abs(sin(vec2(5,10)*q.xz));
    float s = min(u.x,u.y);
    return vec4(3)*smoothstep(.1+.03*x,0.,s)*exp(-.3*x);
}
vec4 scene (vec3 p, vec3 d, float x, vec3 n, float f, vec3 m, mat3 o) {
    vec4 Q = vec4(0);
    d = refract(d,n,eta(f));
    vec4 c = col(f);
    for (float i = 0.; i < 2.; i++) {
        c.xyz *= .9-.4*n;
        p = p+d*x;
        p += .01*d;
        x = scene(p,d,n,1,m,o);
        if (x > 1e5) {
            Q += c*background(p,d);
            break;
        }
        d = refract(d,n,eta(f));
    }
    return Q;
}
Main {
    vec3 m = texelFetch(iChannel0,ivec2(0,0),0).xyz;
    mat3 o = mat3(
        texelFetch(iChannel0,ivec2(2,0),0).xyz,
        texelFetch(iChannel0,ivec2(3,0),0).xyz,
        texelFetch(iChannel0,ivec2(4,0),0).xyz);


        Q = vec4(0);

      for (float x = -AA; x <= AA; x++)
      for (float y = -AA; y <= AA; y++) {
        vec3 p = vec3(m.x,0,-2.3+m.z);
        vec3 d = normalize(vec3(2.*(U-.5*R+.5/(AA==0.?1.:AA)*vec2(x,y))/R.y,1));
        float t = -.2;
        d.yz *= mat2(cos(t),sin(t),-sin(t),cos(t));


        vec3 n;
        float x = scene(p,d,n,0,m,o);
        if (x < 1e7) {
            Q += .3*background(p,reflect(d,n));
            for (float f = 0.; f < 1.; f+=.33) 
                Q += scene(p,d,x,n,f,m,o);

        } else {
            Q += background(p,d);
            float x = iPlane(p-vec3(0,-1,0),d,vec3(0,1,0));
            if (x > 0.) {
                p += d*x;
                d = reflect(d,vec3(0,1,0));
                p += .001*d;
                x = scene(p,d,n,0,m,o);
                if (x < 1e7) {
                    Q += .2*background(p,reflect(d,n));
                    for (float f = 0.; f < 1.; f+=.5) 
                        Q += .1*scene(p,d,x,n,f,m,o);
                } else {
                    Q += .1*background(p,reflect(d,n));
                }
            } 
        }
    }
    Q /= (AA*2.+1.)*(AA*2.+1.);
}

