const float FADE_OUT_START = 2.0;
const float FADE_OUT_DURATION = 0.5;

void mainImage(out vec4 out_color, in vec2 coord) {
    vec2 uv = coord / iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;
    float fade = iTime > FADE_OUT_START
        ? iTime < (FADE_OUT_START + FADE_OUT_DURATION)
            ? 1.0 - (iTime - FADE_OUT_START) / FADE_OUT_DURATION
            : 0.0
        : 1.0;

    out_color = vec4(color * fade, 1);
}

#define pi 3.141592653589793
vec2 rotate(vec2 vec, float rot)
{
    float s = sin(rot), c = cos(rot);
    return vec2(vec.x*c-vec.y*s, vec.x*s+vec.y*c);
}

float sd_box( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime - .5;
    float w = 0.01 + smoothstep(6., 6.5, t) - smoothstep(7., 7.5, t);
    vec2 uv = (fragCoord - iResolution.xy * .5) /iResolution.yy;
    float s = smoothstep(.0, .5, t) * .1
        + smoothstep(2., 2.5, t) * .1
        - smoothstep(4., 4.5, t) * .2
        + smoothstep(5., 5.5, t) * .3
        - smoothstep(7., 7.5, t) * .3
        ;
    uv = rotate(uv, smoothstep(1., 1.5, t) * pi * .25
        + smoothstep(3., 3.5, t) * pi * .25
        + smoothstep(5., 5.5, t) * pi * .25
        );
    float v = min(smoothstep(-w, w, sd_box(uv, vec2(s))), smoothstep(w, -w, sd_box(uv, vec2(s))));
    fragColor = vec4(vec3(v),1.0);
}

