// Light Lanes 0.51.230927 by QuantumSuper
// 2d pseudo-random lanes with light-"sparks" wandering along them
// 


float aaStep( float fun){return smoothstep( min(fwidth(fun),.001), .0, fun);} //simple antialiasing

float hash21(vec2 p){ //pseudorandom generator, see The Art of Code on youtu.be/rvDo9LvfoVE
    p = fract(p*vec2(13.81, 741.76));
    p += dot(p, p+42.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (2.*fragCoord-iResolution.xy) / max(iResolution.x, iResolution.y); //long edge -1 to 1, square aspect ratio
    float amp = 0.;
	float delY = .1;
	float r, ox, fun;

	for (float m=-1.;m<=1.;m+=.05){
		ox = m;
		fun = uv.x-ox;
		for (float n=0.;n<2./delY;n++){ //uv.y -1..1
			r = floor(hash21(vec2(n+ceil(iTime/8.),ox+max(.98,fract(iTime/8.))))*3.-1.); //r {-1,0,1}
            fun += (r*(uv.y-(delY*n-1.))+ox+m) * step(delY*n-1.,uv.y+.001) * step(uv.y+.001,delY*(n+1.)-1.); //+.001 to avoid weird? singularities for negative uv.y
            ox = r*(delY*(n+1.)-1.) - (r*(delY*n-1.)-ox-m) - m; //? isnt that supposed to be: ox += r*delY ?
		}
		amp += aaStep(abs(fun)-.009*iResolution.y/iResolution.x) * (.1+.9*aaStep(abs(fragCoord.y/iResolution.y-fract(iTime/4.+m))-.005*iResolution.y/iResolution.x));
	} 

    fragColor = vec4(vec3(amp),1);
}

// SDFs: https://iquilezles.org/articles/distfunctions2d/
// Sign: https://www.etsy.com/uk/listing/1414248168/fortune-ramen-cat-neon-sign-anime-lucky

float dot2(vec2 A)
{
    return dot(A.xy, A.xy);
}

float saturate(float A)
{
    return clamp(A,0.0,1.0);
}

float sdBezier(vec2 pos, vec2 A, vec2 B, vec2 C )
{    
    vec2 a = B - A;
    vec2 b = A - 2.*B + C;
    vec2 c = a * 2.;
    vec2 d = A - pos;
    float kk = 1./dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.*dot(a,a)+dot(d,b))/3.;
    float kz = kk * dot(d,a);      
    float res = 0.;
    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.*kx*kx-3.*ky)+kz;
    float h = q*q + 4.*p3;
    if( h >= 0.) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h,-h)-q)/2.;
        vec2 uv = sign(x)*pow(abs(x),vec2(1./3.,1./3.));
        float t = clamp(uv.x+uv.y-kx, 0.,1.);
        res = dot2(d+(c + b*t)*t);
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.))/3.;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3  t = clamp(vec3(m+m,-n-m,n-m)*z-kx,0.,1.);
        res = min( dot2(d+(c+b*t.x)*t.x),
                   dot2(d+(c+b*t.y)*t.y) );
        // the third root cannot be the closest
        // res = min(res,dot2(d+(c+b*t.z)*t.z));
    }
    return sqrt( res );
}

