// The sphere is rendered as points, just for illustrative purposes.
// The actual directions are sampled uniformly in cone.

vec3 sphere_pos = vec3(.0,0.4,0.2);
float sphere_rad = 0.3;

mat3 get_orth_basis(vec3 origin, vec3 target){
    vec3 dir = normalize(target - origin);
    vec3 right = normalize(cross(vec3(0,1,0),dir));
    vec3 up = cross(dir, right);
    return mat3(up, right, dir);
}

vec3 sample_cone(vec3 origin, vec3 sphere_pos, float sphere_rad){
    mat3 orth_basis = get_orth_basis(origin, sphere_pos);

    // The apex angle is derived the same way as in https://www.shadertoy.com/view/DtSBD3
    float l = length(sphere_pos.xyz - origin.xyz);
    float apex_angle = asin(sphere_rad/2.0/l)*2.0;

    // --- w is the solid angle, derived from the apex angle
    // w = integrate sin(theta) for theta from 0 to pi/2 for phi from 0 to 2pi)
    // w = (cos(apex_angle/2) + cos(0)) * 2pi

    // pdf(w)       = 1./w
    // --- convert to theta/phi
    // pdf(the,phi) = sin(the) * pdf(w)
    // pdf(phi)     = 1/2pi

    // pdf(theta) = integrate sin(the) * pdf(w) from 0 to 2pi
    // pdf(theta) = sin(theta) * pdf(w) * 2pi

    // --- integrate & inverse to get inverse CDF
    // c = pdf(w) * 2pi 
    // x_theta_integr_0_the = c - c * cos(the)
    // x_theta = acos((c-the)/c)
    // x_phi = phi * 2pi


    vec2 x = hash_v2();

    float w = (-cos(apex_angle/2.0) + cos(0.0)) * 2.*pi;


    float c = 2. * pi * 1./w;
    float theta = acos((c - x.x)/c);
    float phi = x.y * 2. * pi;

    return orth_basis * car_to_pol(theta,phi);
}
vec3 proj(vec3 p){

    vec2 muv = (iMouse.xy - 0.5*R)/R.y;

    if(iMouse.z > 0.5){
        p.xz *= rot(-muv.x*tau/2.0);
        p.yz *= rot(-muv.y*2.6);

    } else {
        p.xz *= rot(iTime*0.4);
    }
    p.y -= 0.5;
    p.z += 1.4;

    p.xy /= p.z;

    return p;
}


void draw_sphere(inout vec3 col, vec2 uv){

    float subdivs_the = 40.;
    float subdivs_phi_max = 40.;

    for(float the = 0.; the <= subdivs_the; the++){
        float the_idx = the/subdivs_the*pi;

        float sc = sqrt(abs(cos(the_idx - pi/2.0)));
        float subdivs_phi = max(subdivs_phi_max*sc,1.0);
        for(float phi = 0.; phi < subdivs_phi; phi++){
            float phi_idx = phi/subdivs_phi * 2. * pi;
            vec3 p = vec3(
                cos(phi_idx) * sc,
                (the/subdivs_the*2. - 1.0),
                sin(phi_idx) * sc
            ) * sphere_rad * 0.5;
            p += sphere_pos;
            p.z += 0.;
            vec3 q = proj(p);
            float sd = sd_dot(uv, q.xy) - 0.002;
            //sd = min(sd, sdSegment(uv, proj(vec3(0)).xy, q.xy) - ln_sz);
            //col = mix(col,c,0.2*smoothstep(fwidth(uv.y),0.,sd));
            col = mix(col,vec3(1),smoothstep(fwidth(uv.y),0.,sd)*smoothstep(4.,0.,q.z));
        }
    }

}

void draw_floor(inout vec3 col, vec2 uv){
    float iters = 10.;
    float range = 2.;
    for(float axis = 0.; axis < 2.0; axis++){
        for(float z = 0.; z <= iters; z++){
            float zidx = z/iters;

            float pz = zidx * range - 0.5*range;
            vec3 pa = vec3(-1,0,pz);
            vec3 pb = vec3(1,0,pz);
            if(axis > 0.5){
                pa = vec3(pz,0,-1);
                pb = vec3(pz,0,1);
            }

            vec3 qa = proj(pa);
            vec3 qb = proj(pb);
            if(qa.z > 0.0 && qb.z > 0.){
                float sd = sdSegment(uv, qa.xy, qb.xy) - 0.001;
                col = mix(col,vec3(1.0),0.9*smoothstep(fwidth(uv.y),0.,sd));
            }

        }
    }
}

void mainImage( out vec4 C, in vec2 U){

    vec2 uv = (U - 0.5*R)/R.y;

    vec3 col = vec3(0.);

    sphere_pos += sin(
        vec3(
            sin(iTime*1.1),
            sin(iTime*0.7),
            sin(iTime*0.8)
        )
    )*0.2;
    sphere_rad += sin(iTime + sin(iTime))*0.2;


    draw_floor(col, uv);

    float pt_sz = 0.01;
    float ln_sz = 0.001;
    float opacity = 0.2;

    //seed += uint(iFrame);
    for(float i = 0.; i < 525. + min(iTime,0.); i++){
        vec3 c = oklch_to_srgb(vec3(0.7,0.3,hash_f()*tau));
        vec3 p = vec3(0,0,0.);
        vec3 s = sample_cone(p, sphere_pos, sphere_rad);
        p += s;

        vec3 q = proj(p);
        float sd = sdSegment(uv, proj(vec3(0,0.,0)).xy, q.xy) - ln_sz;
        col = mix(col,c*2.,0.1*smoothstep(fwidth(uv.y),0.,sd));

        sd = sd_dot(uv, q.xy) - pt_sz;
        col = mix(col,c*2.,0.3*smoothstep(fwidth(uv.y),0.,sd));
    }

    draw_sphere(col, uv);

    col *= 1.;
    col = 1.-col;
    col = 1.-exp(-abs(col));
    col = pow(col,vec3(0.4545));
    //col = pow(col,vec3(2.4545));
    //col = smoothstep(vec3(0),vec3(1.0),col);
    C = vec4(col,1.0);
}

