#define R iResolution.xy
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define pi acos(-1.)
#define tau (2.*pi)



uint seed = 154125u;


uint hashi(inout uint x)
{
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}
uint wang(uint a) {
	a = (a ^ 61U) ^ (a >> 16U);
	a = a * 9U;
	a = a ^ (a >> 4);
	a = a * 0x27d4eb2dU;
	a = a ^ (a >> 15);
	return a;
}
#define hash_f_s(s)  ( float( hashi(uint(s)) ) / float( 0xffffffffU ) )

#define hash_f()  ( float( seed = hashi(seed) ) / float( 0xffffffffU ) )
#define hash_v2()  vec2(hash_f(),hash_f())
#define hash_v3()  vec3(hash_f(),hash_f(),hash_f())
#define hash_v4()  vec3(hash_f(),hash_f(),hash_f(),hash_f())



float sd_dot(vec2 uv, vec2 p){
    return length(uv - p);
}
float sdSegment( in vec2 p, in vec2 a, in vec2 b ){
