    V = clamp(V,vec3(0),R3*vec3(1,1,6)-vec3(0,0,1));
    int i = int(floor(V.z/R3.z));
    V.z = mod(V.z,R3.z);
    return H(_3D(V),i);
}
vec4 T (vec3 u)  {
    vec3 f = floor(u);
    vec3 c = ceil(u);
    vec4 _000 = G(f);
    vec4 _001 = G(vec3(f.xy,c.z));
    vec4 _010 = G(vec3(f.x,c.y,f.z));
    vec4 _011 = G(vec3(f.x,c.yz));
    vec4 _100 = G(vec3(c.x,f.yz));
    vec4 _101 = G(vec3(c.x,f.y,c.z));
    vec4 _110 = G(vec3(c.xy,f.z));
    vec4 _111 = G(c);
    vec4 _00 = mix(_000,_001,fract(u.z));
    vec4 _01 = mix(_010,_011,fract(u.z));
    vec4 _10 = mix(_100,_101,fract(u.z));
    vec4 _11 = mix(_110,_111,fract(u.z));
    vec4 _0 = mix(_00,_01,fract(u.y));
    vec4 _1 = mix(_10,_11,fract(u.y));
    return mix(_0,_1,fract(u.x));
}
vec3 vel (vec3 v) {
    return v*inversesqrt(1.+dot(v,v));
}
void mainCubemap( out vec4 Q, in vec2 U, in vec3 _, in vec3 d )
{
    if (U.x>R2.x||U.y>R2.y) {Q = vec4(0); return;}
    int f = face(d);
    vec3 V = _3D(floor(U))+R3*vec3(0,0,f);
    Q = G(V);
    vec4 dQ = vec4(0);
    if (iFrame % 2 == 0) {
        for(int x=-1;x<=1;x++)
        for(int y=-1;y<=1;y++)
        for(int z=-1;z<=1;z++)  if(abs(x)!=abs(y)&&abs(y)!=abs(z))
        {
            vec3 v = vec3(x,y,z);
            vec3 w = V+v;
            if (w.x<2.||w.y<2.||w.z<2.||R3.x-w.x<2.||R3.y-w.y<2.||6.*R3.z-w.z<2.)
            {
                dQ += 1./6.*Q.w*vec4(Q.xyz,1);
            }
            else 
          {
              vec4 q = G(w);
            vec3 a = vel(Q.xyz),
                 b = vel(q.xyz)+v;
           float ab = dot(v,b-a);
           float i = dot(v,(0.5*v-a))/ab;
               float j = .5+.5*max(1.-q.w,0.);
               float k = .5+.5*max(1.-Q.w,0.);
               float wa = (1./6.+.00005)*Q.w*min(i,j)/j;
               float wb = (1./6.+.00005)*q.w*max(k+i-1.,0.)/k;
               dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
          }

        }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;
    if (V.x < 2. || V.y < 2.|| V.z < 2.|| 
            R3.x-V.x < 2.|| R3.y-V.y < 2. || R3.z*6.-V.z < 2.)
            Q *= 0.;
    } else {
        Q.y -= .0005;
        for(int x=-1;x<=1;x++)
        for(int y=-1;y<=1;y++)
        for(int z=-1;z<=1;z++)  if(abs(x)!=abs(y)&&abs(y)!=abs(z)) {
            vec3 v = vec3(x,y,z);
            vec4 q = G(V+v);
            Q.xyz -= .06*q.w*(q.w-1.)*v;
        }
        if (V.x < 2. || V.y < 2.|| V.z < 2.|| 
            R3.x-V.x < 2.|| R3.y-V.y < 2. || R3.z*6.-V.z < 2.)
            Q *= vec4(0,0,0,.99);

        //float t = float(iFrame)/200.;
        //if (length(V-vec3(.1,.8,.5*6.)*R3) < 8.) Q = vec4(+.8+.0*sin(t),.0*cos(t),.05*sin(.76*t),1);
        //if (length(V-vec3(.9,.8,.5*6.)*R3) < 8.) Q = vec4(-.8+.0*sin(t),.0*cos(t),.05*sin(.86*t),1);

    }
    if (iFrame < 100) {
        if (V.y < .4*R3.y) Q.w = 1.+.003*(.4*R3.y-V.y);
        if (length(V-vec3(.5,.8,3.)*R3) < 25.) Q.w = .8,Q.y=-.6;
    }
}

