#define sat(a) clamp(a, 0., 1.)
float _sqr(vec2 uv, vec2 s)
{
    vec2 l = abs(uv)-s;
    return max(l.x, l.y);
}

float hash(float seed)
{
    return fract(sin(seed*123.456)*123.456);
}

mat2 r2d(float a) {
    float c = cos(a), s = sin(a); 
    return mat2(c, -s, s, c);
}
// Thanks IQ :)
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }
vec3 rdrtree(vec2 uv, vec3 col, vec3 light)
{
  float sharp = iResolution.x*.5;
  uv.x+= sin(uv.y*5.+uv.x*2.+iTime)*.02*sat(uv.y);
  vec2 p = uv;
  float trunk = 1000.;
  for (float i = 0.;i<6.;++i)
  {
    float off = mix(.3,.05,i/7.);
    p+= vec2(0.,-off*.5);
    p*=r2d(.5*sat(i));
    p+= vec2(0.,-off*.5);
    p.x+= sin(p.y*20.)*.02;
    p.x = abs(p.x);
    vec2 pos = p;

    float shape = _sqr(pos, vec2(.01,off*.45));
    shape -=.0001
    -texture(iChannel0,uv*.15).x*.007;
    if(i<4.5)
    {
        trunk = smin(trunk, shape, .05);
     }
   else
    {
      float cir = length(p)-.06;
      col = mix(col, vec3(0.957,0.333,0.286)*.7+light,
        1.-sat(cir*sharp));

    }
  }
  col = mix(col, vec3(0.086,0.173,0.145)*.05+light*.75,1.-sat(trunk*sharp));

  return col;
}

float cloud(vec2 uv)
{
    return 
    (texture(iChannel0, uv*.05).x*.5
    +texture(iChannel0, uv*.1*r2d(iTime*.05+uv.x*.2)).x*.2
    +texture(iChannel0, uv*.2*r2d(-iTime*.02+uv.y*.5)).x*.2
    +texture(iChannel0, uv*.33).x*.07)
    *sat(texture(iChannel0, uv*.01).x-.2)
    ;
}

vec3 rdr(vec2 uv)
{
    float sharp = iResolution.x*.5;
    vec3 col = vec3(0.110,0.153,0.133)*(1.-sat(length(uv-.5)));
    float fins = sat(200.*sin(uv.y*200.));
    float shadow = rdrtree(uv*2.-vec2(1.,-0.5), vec3(1.), vec3(0.)).z;
    float boxwin = _sqr(uv-vec2(.4,.5), vec2(.2,.4));
    col = mix(col, vec3(0.957,0.333,0.286), sat(.7+fins)*shadow*(1.-sat(boxwin*sharp)));
    float boxwin2 = _sqr(uv-vec2(.4,.5)-.2, vec2(.2,.4));
    vec3 light = 2.*vec3(0.957,0.333,0.286)*(1.-sat(boxwin2*sharp));

    col = rdrtree(uv*2.-vec2(1.2,0.2), col, .25*light*sat(.7+fins));

    float box = _sqr(uv-vec2(.6,.1), vec2(.05,.1));
    col = mix(col, vec3(0.), 1.-sat(box*sharp));

    col += .5*vec3(0.871,0.647,0.627)*cloud(uv)*sat(.7*length(uv-.5));
       float boxwin3 = _sqr(uv-vec2(.4,.5)-.25, vec2(.2,.4));

         float finssmoke = mix(1.,sat((sin(uv.y*150.)*.5+.5)+.5), 1.-sat(boxwin3*sharp*.1));
        float beat = texture(iChannel1, vec2(0.1, 0.)).x;
        col += mix(0.5,2.,beat)*finssmoke*2.*vec3(0.631,0.690,0.871)*cloud(-uv*1.)*(1.-sat(1.7*length(uv-.5)));
    col += 2.*vec3(0.957,0.333,0.286)*(1.-sat(length(uv-.5)*1.5))*.5;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    vec3 col = vec3(0.);
    col = rdr(uv*1.5+.5);
    col = pow(col, vec3(1.5));
    //col += texture(iChannel0, uv).x;
    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 A, vec2 F) {
    vec2 r = iResolution.xy, u = (F+F-r)/r.y;    
    A.rgb*=0.;

    float autoStartTime = 0.0;
    float timeFactor = 2.0; // mvmnt speed

    for (float i; i<25.; A.rgb +=
        .003/(abs(length(atan(u*u*u-u*u*u)-+tanh(u/u*u)/exp2(u*u))-i*.05)+.0009)
        * (cos(i+vec3(3.9,5.,2.54534))+001.)                           
        * smoothstep(.5,.9, abs(tan((length(mod(max(iTime-autoStartTime, 0.0),2.)-i*.1)-1.))))
    ) {
        // smoother mvmnt
        float oscillation = atan(iTime * timeFactor) * 0.1;
        u += oscillation * vec2(sin(iTime), sin(iTime));

        // mvmnt transformations
        u *= mat2(cos((iTime+autoStartTime+i++)*.01 + vec4(0,33,11,0)));
    }

    // audio
    float audioAmplitude = texture(iChannel0, vec2(1.)).r * 2.0; 
    A.rgb *= audioAmplitude;
}

#define SAMPLE_WIDTH 8.0

vec3 yLine(vec2 uv,float y, float thickness){
    float colwave = thickness /  abs(uv.y+y);
    return vec3(colwave);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) { 
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 col = vec3(0.0);

    // Average together some of the lower samples to get the circle to pulse to the music.
    float avgAmp = 0.0;
    for (float i = 0.0; i < SAMPLE_WIDTH; ++i)
    {
        avgAmp += texelFetch(iChannel0, ivec2(8 + int(i), 1), 0).x * (1.0 / SAMPLE_WIDTH);
    }
    // Most times the average Amplitude is between [0.1, 0.9], so we map that range to 0, 1
    // for a fuller pulsing effect.
    avgAmp = (clamp(avgAmp, 0.1, 0.9) - 0.1) * 1.25;

    col += length(uv) / max(avgAmp, 0.1);
    col += yLine(uv, sin(uv.x + iTime * 1.0) + sin(uv.y + iTime * 1.0), 0.01);
    col += yLine(uv, sin(uv.x + iTime * 0.2) + cos(uv.y + iTime * 2.0), 0.01);
    col += yLine(uv, sin(uv.x + iTime * 4.0) + sin(uv.y + iTime * 0.5), 0.01);
    col += yLine(uv, cos(uv.x + iTime * 0.2) + sin(uv.y + iTime * 1.5), 0.01);
    // In the original the color keeps increasing past the edge of the circle so the whole screen is white,
    // this makes the color falloff back to zero the brighter it gets so we get a ring.
    col = max(-abs(col - 2.0) + 2.0, 0.0);

    // Change ivec2 x values to sample different frequencies.
    float r = avgAmp * col.x;
    float g = texelFetch(iChannel0, ivec2(250,0), 0).x * col.y;
    float b = texelFetch(iChannel0, ivec2(500,0), 0).x * col.z;

    // Takes the lowest color value and reduces it by the min difference between the other two color channels.
    // This is done to have colors pop more often and only be white when all frequencies are around the same
    // amplitude.
    if (r < g && r < b)
    {
        r *= 1.0 - max(g - r, b - r);
    }

    if (g < r && g < b)
    {
        g *= 1.0 - max(r - g, b - g);
    }

    if (b < r && b < g)
    {
        b *= 1.0 - max(r - b, g - b);
    }

    vec3 finalColor = col * vec3(r,g,b);
    fragColor = vec4(finalColor, 1.0);
}

float s;

mat2 rot(float a)
{
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}


vec3 path(float z)
{
    vec3 p = vec3(sin(z) * .5, cos(z * .5), z);
    p.x+=p.y*p.x*2.;
    return p;
}

vec3 fractal(vec3 p)
{
    float z = p.z * .1;
    p.z = abs(.5 - fract(z));
    float m = 100.;
    for (int i = 0; i < 10; i++)
    {
        p.xy *= rot(z);
        p = abs(p * (1.5+sin(z*3.)*.2)) - 2.;
        m = min(m, abs(p.y) + .5 * abs(.5 - fract(p.x * .25 + iTime + float(i) * .1)));
    }
    m = exp(-8. * m) * 2.;
    return vec3(p.xz * 2., m) * m + .5;
}

float g=0.;
vec3 lpos;

float de(vec3 p)
{
    float d=length(p-lpos)-s*.2;
    p.xy -= path(p.z).xy;
    g+=.003/(.1+d*5.);
    return min(d,-length(p.xy) + .3);
}

vec3 march(vec3 from, vec3 dir)
{
    float d, td = 0.;
    vec3 p, col = vec3(0);
    for (int i = 0; i < 80; i++)
    {
        p = from + dir * td;
        d = de(p);
        if (d < .001) break;
        td += d;
    }
    if (d < .1) 
    {
        p -= .001 * dir;
        col = fractal(p) * exp(-1. * td * td) * smoothstep(.3, 1., td);
    }
    return (col+g)*(.3+s*4.);
}

mat3 lookat(vec3 dir, vec3 up) {
    dir = normalize(dir);
    vec3 rt = normalize(cross(dir, normalize(up)));
    return mat3(rt, cross(rt, dir), dir);
}

float getSound() 
{
    float s=0.;
    for (float i=0.; i<20.; i++) {
        s+=texture(iChannel0,vec2(0.,i/20.)).r;
        s+=texture(iChannel0,vec2(i/20.,0.)).r;
    }
    return s/20.;
}

void texto(inout vec3 col, vec2 offset) {
    vec2 uv=gl_FragCoord.xy/iResolution.xy;
    uv.y=1.-uv.y;
    vec4 tx = texture(iChannel1, uv+offset);
    col = mix(col, tx.rgb, length(tx.rgb));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     s=getSound();
     vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    float t = iTime;
    vec3 from = path(t);
    lpos = path(t+1.);
    vec3 fw = normalize(path(t + .5) - from);
    vec3 dir = normalize(vec3(uv, 1));
    dir = lookat(fw, vec3(0, 1, 0)) * dir;
    vec3 col = march(from, dir);
    texto(col, vec2(-0.35,-0.4));
    fragColor = vec4(col,1.0);
}

