#ifdef ANOTHER_ALGORITHM
float dseg1(vec2 p0, vec2 p1)
{
	vec2 cp = vec2(uv.x-(uv.y*.5-ch_pos.y*.5-p0.y), uv.y) - ch_pos - p0;
