int face (vec3 dir){
    if        (dir.x>max(abs(dir.y),abs(dir.z))){
        return 0;
    } else if (dir.y>max(abs(dir.z),abs(dir.x))){
        return 1;
    } else if (dir.z>max(abs(dir.x),abs(dir.y))){
        return 2;
    } else if (-dir.x>max(abs(dir.y),abs(dir.z))){
        return 3;
    } else if (-dir.y>max(abs(dir.z),abs(dir.x))){
        return 4;
    } else if (-dir.z>max(abs(dir.x),abs(dir.y))){
        return 5;
    } else return -1;
}
vec4 H (vec2 U, int i) {
    ivec3 p = ivec3(U,i);
    vec2 x = vec2(p-512) + 0.5;
    vec3 d;
           if (p.z==0){     d = vec3( 512,-x.y,-x.x);
    } else if (p.z==1){     d = vec3( x.x, 512, x.y);
    } else if (p.z==2){     d = vec3( x.x,-x.y, 512);
    } else if (p.z==3){     d = vec3(-512,-x.y, x.x);
    } else if (p.z==4){     d = vec3( x.x,-512,-x.y);
    } else if (p.z==5){     d = vec3(-x.x,-x.y,-512);
    } else d = vec3(0);
    return texture(iChannel0,d);
}
vec2 vel(vec2 v) {
    return .5*v*inversesqrt(1.+.1*dot(v,v));
}
void mainCubemap( out vec4 Q, in vec2 U, in vec3 _, in vec3 d )
{
    int f = face(d);
    Q = H(U,f);
    if (iFrame % 2 == 0) {
        for(int x=-1;x<=1;x++)
        for(int y=-1;y<=1;y++)  if(abs(x)!=abs(y))
        {
            vec2 u = vec2(x,y);
            vec4 q = H(U+u,f);
            float k = 0.;
            for(int i = 0; i < 6; i++)   {
                k += H(U+u,i).w;
            }
            Q.xy -= .08*(q.w*(q.w-1.)+k)*u;
        }
        Q.y -= 3e-4*sin(float(f));
        if (length(Q.xy)>.5)Q.xy = .5*normalize(Q.xy);
        if (U.x < 3.||1024.-U.x < 3.) Q.x *= 0.;
        if (U.y < 3.||1024.-U.y < 3.) Q.y *= 0.;
        if (iFrame < 3) {
            Q.w = .2+.1*sin(.5*U.x+float(f))+.1*sin(.5*U.y+float(f*f));
        }

    } else {

        vec4 dQ = vec4(0);

        for(int x=-1;x<=1;x++)
        for(int y=-1;y<=1;y++)  if(abs(x)!=abs(y))
        {
            vec2 u = vec2(x,y);
            {
                vec4 q = H(U+u,f);
                vec2 a = vel(Q.xy),
                     b = vel(q.xy)+u;
               float ab = dot(u,b-a);
               float i = dot(u,(0.5*u-a))/ab;
              {
                   float j = .5+.5*max(1.-q.w,0.);
                   float k = .5+.5*max(1.-Q.w,0.);
                   float wa = 0.25008*Q.w*min(i,j)/j;
                   float wb = 0.25008*q.w*max(k+i-1.,0.)/k;
                    dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
              }
           }

        }
        if (dQ.w>0.)dQ.xyz/=dQ.w;
        Q = dQ;
    }
}

