const float r0 = 0.025;
const float r1 = r0 * sqrt(m1);
const float r2 = r0 * sqrt(m2);
const float thk = 0.0025;
vec3 draw_pendulum(vec3 color, vec2 uv, vec4 state) {
    float th1 = state.x, th2 = state.y, w1 = state.z, w2 = state.w;
    vec2 pos1 = l1 * vec2(sin(th1), -cos(th1));
    vec2 pos2 = pos1 + l2 * vec2(sin(th2), -cos(th2));
    color = mix(vec3(0.9), color, smoothstep(0.0, aa, sdf_circle(uv, 0.01)));
    color = mix(vec3(0.9), color, smoothstep(0.0, aa, sdf_circle(uv - pos1, r1)));
    color = mix(vec3(0.9), color, smoothstep(0.0, aa, sdf_circle(uv - pos2, r2)));
    color = mix(vec3(0.9), color, smoothstep(0.0, aa, sdf_seg(uv, vec2(0.0), pos1, thk)));
    color = mix(vec3(0.9), color, smoothstep(0.0, aa, sdf_seg(uv, pos1, pos2, thk)));
    return color;
}

vec3 draw_energy_bar(vec3 color, vec2 uv, vec4 state) {
    float E0 = energy(state_0);
    float E = energy(state);
    float val = 0.1 * (E - E0) / abs(E0) + 0.5;
    val = clamp(val, 0.0, 1.0);
    float r = 0.02;
    vec2 dim = vec2(0.01, 0.3);
    vec2 pos = vec2(0.0, -(1.0-val)*dim.y);
    float box = smoothstep(0.0, aa, sdf_box(uv-pos, dim*vec2(1.0, val)));
    float border = smoothstep(0.0, aa, sdf_hollow_box(uv, dim+r, 0.0025));
    vec2 v = vec2(dim.x+r*0.5, 0.0);
    float line = smoothstep(0.0, aa, sdf_seg(uv,-v,v, 0.0025));
    color = mix(vec3(0.9), color, box);
    color = mix(vec3(0.9), color, border);
    color = mix(vec3(0.9), color, line);
    return color;
}

void mainImage(out vec4 frag_color, in vec2 frag_coord) {
    vec2 uv = (frag_coord * 2.0 - iResolution.xy) / iResolution.y;
    vec4 state = fetch(iChannel0, STATE_ADDR);

    vec3 color = vec3(0.2);
    color = draw_pendulum(color, uv, state);
    if (iMouse.z > 0.0) color = draw_energy_bar(color, uv - vec2(-1.5, 0.0), state);

    vec3 trail = fetch(iChannel1, frag_coord).rgb;
    color += trail;

    frag_color = vec4(color, 1.0);
}

