vec4 f(vec4 state) {
    float th1 = state.x, th2 = state.y, w1 = state.z, w2 = state.w;
    float cth = cos(th1-th2), sth = sin(th1-th2);
    float den = m1+m2*sth*sth;
    float a1 = (m2*g*sin(th2)*cth-m2*sth*(l1*w1*w1*cth+l2*w2*w2)-(m1+m2)*g*sin(th1))/(l1*den);
    float a2 = ((m1+m2)*(l1*w1*w1*sth-g*sin(th2)+g*sin(th1)*cth)+m2*l2*w2*w2*sth*cth)/(l2*den);
    a1 -= damping * w1;
    a2 -= damping * w2;
    return vec4(w1, w2, a1, a2);
}

vec4 simple_integrate(vec4 state, float h) {
    return state + h * f(state);
}

vec4 rk_integrate(vec4 state, float h) {
    vec4 k1 = h*f(state);
    vec4 k2 = h*f(state + 0.5*k1);
    vec4 k3 = h*f(state + 0.5*k2);
    vec4 k4 = h*f(state + k3);
    return state + (k1+2.0*k2+2.0*k3+k4)/6.0;
}

void mainImage(out vec4 state, in vec2 frag_coord) {
    if (to_store(frag_coord, STATE_ADDR)) {
        state = state_0;
    } else if (to_store(frag_coord, PREV_STATE_ADDR)) {
        state = state_0;
    }
    if (iFrame > 0) {
        if (to_store(frag_coord, STATE_ADDR)) {
            state = fetch(iChannel0, STATE_ADDR);
            float dt = iTimeDelta;
            state = rk_integrate(state, dt);
        } else if (to_store(frag_coord, PREV_STATE_ADDR)) {
            state = fetch(iChannel0, STATE_ADDR);
        }
    }
}

