#define fetch(buf, addr) texelFetch(buf, ivec2(addr), 0)
#define to_store(buf_pos, addr) (ivec2(buf_pos) == addr)

const ivec2 STATE_ADDR = ivec2(0, 0);
const ivec2 PREV_STATE_ADDR = ivec2(1, 0);
const float pi = 3.14159265359;
const float l1 = 0.4;
const float l2 = 0.5;
const float g = 9.81;
const float m1 = 1.0;
const float m2 = 1.0;
const vec4 state_0 = vec4(0.5*pi, 1.5*pi, 0.0, -10.0);
const float damping = 0.0;

// anti-aliasing
const float aa = 0.0025;

float energy(vec4 state) {
    float th1 = state.x, th2 = state.y, w1 = state.z, w2 = state.w;
    float T = 0.5*((m1+m2)*l1*l1*w1*w1+m2*l2*l2*w2*w2+m2*l1*l2*w1*w2*cos(th1-th2));
    float V = -(m1+m2)*g*l1*cos(th1)-m2*g*l2*cos(th2);
    return T + V;
}

float sdf_circle(vec2 p, float r) {
    return length(p) - r;
}

float sdf_seg(vec2 p, vec2 a, vec2 b, float r) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    return length(pa - ba*h) - r;
}

float sdf_box(vec2 p, vec2 b) {
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

float sdf_hollow_box(vec2 p, vec2 b, float r) {
    return abs(sdf_box(p, b)) - r;
}

