#define itr 50          //number of iterations
#define maxL 1000.      //maxiumum escape length
#define fade .33        //new to old image update ratio
#define eqBands 12.     //number of equalizer bands to track
#define freqPerBand 20. //subdivision of equalizer bandss
#define reactAmt 0.02   //audio react ammount
#define sweepAmt 1.0    //cofficient sweep ammount
#define useMax 0        //equalizer band method; 0=average, 1=maximum

//rotation matrix
mat2 rotM(float a)
{
	a *= 3.14159 / 180.0;
    float s=sin(a), c=cos(a);
    return mat2( c, -s, s, c );
}

//get value of EQ band
float getBand(float x)
{
    float band = floor(x*eqBands);
    float coarseSpacing = 1.0 / eqBands;
    float fineSpacing = coarseSpacing / freqPerBand;
    float start = coarseSpacing * band;
    float val = 0.0;
    float c;
    for(float i=0.0; i < freqPerBand; i++)
    {
        c=(start + fineSpacing * i);
        c = c*c; //better frequency response curve
        #if useMax
            val = max(val, texture(iChannel0, vec2(c, 0.25)).r);
        #else
            val += texture(iChannel0, vec2(c, 0.25)).r;
        #endif
    }

    if (useMax==0) val /= freqPerBand;
    return val;
}


//iterate formula
int iterate(vec2 p, out float minD, out float maxD, out vec2 lastP){
    //coefficients
    float a1 =  -0.1 + reactAmt * getBand(0.0 / 12.0);
    float a7 =  -0.9 + reactAmt* getBand(1.0 / 12.0);
    float a2 =  -1.5  + reactAmt * getBand(2.0 / 12.0) + sweepAmt * sin(iTime/5.);
    float a8 =   0.9 + reactAmt * getBand(3.0 / 12.0) + sweepAmt * cos(iTime/7.);   
    float a3 =  -0.5 + reactAmt * getBand(4.0 / 12.0);
    float a9 =   0.1 + reactAmt * getBand(5.0 / 12.0) + sweepAmt *cos(iTime/11.);   
    float a4 =   0.1+ reactAmt * getBand(6.0 / 12.0) + sweepAmt * sin(iTime/17.);
    float a10 = -0.3+ reactAmt * getBand(7.0 / 12.0);  
    float a5 =  -0.8 + reactAmt * getBand(8.0 / 12.0);
    float a11 = -1.5 + reactAmt * getBand(9.0 / 12.0);
    float a6 =   0.2+ reactAmt * getBand(10.0 / 12.0);
    float a12 =  0.0 + reactAmt * getBand(11.0 / 12.0)+ sweepAmt * cos(iTime/13.); 
    float x2, y2, xy;
    vec2 o = p;  //point origin
    float d;     //distance from origin
    minD=1.0;    //max distance
    maxD=0.0;    //min distance 
    int i=0;
    while (i < itr){
        x2 = p.x * p.x;
        y2 = p.y * p.y;
        xy = p.x * p.y;
        p = vec2
        (
            a1 + a2*p.x + a3 *x2 + a4 *xy + a5 *p.y + a6 *y2, 
            a7 + a8*p.x + a9 *x2 + a10*xy + a11*p.y + a12*y2
        ); 
        if (dot(p,p) > maxL*maxL) break;
        d = (o.x-p.x)*(o.x-p.x) + (o.y-p.y)*(o.y-p.y);   //dot product works for smooth values, but doesn't produce sharp bands
        minD = min(d, minD);
        maxD = max(d, maxD);
        i++;
    }
    minD = sqrt(minD);
    maxD = sqrt(maxD);
    lastP = p;
    return i;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvF = (-iResolution.xy + 2.0 * fragCoord)/iResolution.y* rotM(500.*sin(iTime / 23.));
    vec2 lastP;

    float zoom = 20. + 3.5* getBand(0.) + 20.*pow(0.5 + .5* cos(iTime/3.),1.5);
    float minD, maxD;

    //Measure how many iterations it takes to escape bounds
    int i = iterate((uvF*20.0 - vec2(0.0,10.0))/zoom, minD, maxD, lastP);


    //smoothing function
    float t = float(i) - log2(log2(dot(lastP,lastP))) + 4.0;
    t = t/float(itr);

    //mix existing image and new to temporally smooth pixel values
    vec3 ghost = texture(iChannel1, uv).rgb;
    ghost=max(vec3(0.),ghost);

    //color outside of fractal
    vec3 col = 0.5 - 0.5 * sin(0. + 26. * t + (vec3(0.1,0.6,1.0) + maxD/maxL*smoothstep(0.1,0.8,max(0.0,getBand(1.)))));

    //use alternate black and white coloring inside fractal bounds
    if(i >= itr-1) col = 0.5 + 0.5*cos(maxD/minD*vec3(1.0)); 

    fragColor = vec4(mix(ghost, col, fade),1.0);
}

