void magnet_magnet(inout obj o, obj q) {
    vec2 e = vec2(5e-3,0);
    vec3 r = (o.m-q.m)*q.io;
    float k = K;

    float b0 = dot(B(r,.99*_s_,vec3(0),vec3(1,0,0))*q.o,vec3(1,0,0)*o.o);
    float bx = dot(B(r+e.xyy,.99*_s_,vec3(0),vec3(1,0,0))*q.o,vec3(1,0,0)*o.o);
    float by = dot(B(r+e.yxy,.99*_s_,vec3(0),vec3(1,0,0))*q.o,vec3(1,0,0)*o.o);
    float bz = dot(B(r+e.yyx,.99*_s_,vec3(0),vec3(1,0,0))*q.o,vec3(1,0,0)*o.o);

    o.f += k*vec3(bx-b0,by-b0,bz-b0)/e.x;

    o.t += k*cross(vec3(1,0,0),B(r,vec3(_s_),vec3(0),vec3(1,0,0))*q.o);

}
Main {

