    }

    boundaries(me);
    me.v = dt*me.f + me.v*.99;
    me.m = dt*K5*me.f + dt*me.v + me.m;
    me.s = dt*me.t + me.s;
    me.o = me.o * matrix(K5*dt*me.s+dt*me.t);
    me.o[0] = normalize(cross(me.o[1],me.o[2]));
    me.o[1] = normalize(cross(me.o[2],me.o[0]));
    me.o[2] = normalize(cross(me.o[0],me.o[1]));
