    if (x==0) return vec4(o.m,1);
    if (x==1) return vec4(o.o[0],1);
    if (x==2) return vec4(o.o[1],1);
    if (x==3) return vec4(o.o[2],1);
    if (x==4) return vec4(o.v,1);
    if (x==5) return vec4(o.s,1);
    o.io = inverse(o.o);
    if (x==6) return vec4(o.io[0],1);
    if (x==7) return vec4(o.io[1],1);
    if (x==8) return vec4(o.io[2],1);
}

