void triangle_triangle (inout obj o, inout obj q, vec3 a, vec3 b, vec3 c, vec3 e, vec3 g, vec3 h) {
    vec3 _, f;
    a = a*o.o+o.m;
    b = b*o.o+o.m;
    c = c*o.o+o.m;
    e = e*q.o+q.m;
    g = g*q.o+q.m;
    h = h*q.o+q.m;
    if (line_triangle(a,b,e,g,h ,_,f)) {
        f += K2*(velocity(q,_)-velocity(o,_));
        impulse(o,_,f);
    }
    if (line_triangle(b,c,e,g,h ,_,f)) {
        f += K2*(velocity(q,_)-velocity(o,_));
        impulse(o,_,f);
    }
    if (line_triangle(c,a,e,g,h ,_,f)) {
        f += K2*(velocity(q,_)-velocity(o,_));
        impulse(o,_,f);
    }    

    if (line_triangle(e,g,a,b,c ,_,f)) {
        f = -f+ K2*(velocity(q,_)-velocity(o,_));
        impulse(o,_,f);
    }
    if (line_triangle(g,h,a,b,c ,_,f)) {
        f = -f+ K2*(velocity(q,_)-velocity(o,_));
        impulse(o,_,f);
    } 
    if (line_triangle(h,e,a,b,c ,_,f)) {
        f = -f+ K2*(velocity(q,_)-velocity(o,_));
        impulse(o,_,f);
    }
}
