// VISUAL | ZHONK VISION

uniform sampler2D; // Audio input texture

vec3 palette(float d) {
    return mix(vec3(0.4, 5.7, 0.9), vec3(1.0, 0.0, 1.0), d);
}

vec2 rotate(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat2(c, s, -s, c);
}

float map(vec3 p) {
    float angle = iTime * 0.05;
    for (int i = 0; i < 10; ++i) {
        float t = angle * 1.89;
        p.xz = rotate(p.xz, t * 1.89);
        p.xy = rotate(p.xy, t * 1.89);
        p.xz = abs(p.xz);
        p.xz -= 0.5;
    }

    float SHEYTAN = length(p.xy) - 0.9; // Create a circular Sheytan shape

    return SHEYTAN;
}

vec4 rm(vec3 ro, vec3 rd) {
    float t = 0.5;
    vec3 col = vec3(0.0);
    float d;
    for (float i = 0.0; i < 64.0; i++) {
        vec3 p = ro + rd * t;
        d = map(p) * 0.5;
        if (d < 0.02) {
            break;
        }
        if (d > 100.0) {
            break;
        }
        col += palette(length(p) * 0.1) / (400.0 * d);
        t += d;
    }

    // Use the red channel of the audio input texture to modulate the color
    vec3 audioColorMod = texture(iChannel0, vec2(iTime * 0.01, 0.8)).rgb;
    col *= audioColorMod;

    return vec4(col, 1.0 / (d * 80.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - (iResolution.xy / 2.0)) / iResolution.x;
    vec3 ro = vec3(0.0, 0.0, -160.0);

    // Remove rotation modulation
    ro.xz = rotate(ro.xz, iTime * 0.2); // Keep a constant rotation speed

    vec3 cf = normalize(-ro);
    vec3 cs = normalize(cross(cf, vec3(0.0, 1.0, 0.0)));
    vec3 cu = normalize(cross(cf, cs));

    vec3 uuv = ro + cf * 3.0 + uv.x * cs + uv.y * cu;

    vec3 rd = normalize(uuv - ro);

    vec4 col = rm(ro, rd);

    fragColor = col;
}

// ---------------------------------------------------------------------------------------
//	Created by fenix in 2023
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  Inspired by Pezzza's Work on YouTube:
//
//      https://www.youtube.com/watch?v=9IULfQH7E90
//
//  I always wanted to try one of those determistic effects, such as those ball sorters:
//
//      dr2's Magic Ball Sorter      https://shadertoy.com/view/tsGyDy
//
//  I thought Pezzza's Work's idea of using the determinism to assemble an image seemed
//  smart. His simulation looks better than mine, but he has the luxury of iterations.
//
//  Of course the way this works is determinism: the simulation is the same every time
//  it runs, so we only have to run it once to find out where each particle will end
//  up. This is why there is no image on the first run: we don't know yet where the
//  particles will go.
//
//  I had to reuse the shock direction idea to make the particles stable enough for this
//  to work. So here's a proof of concept for the same idea with a verlet simulation. The
//  shock direction modifies the collision stiffness, so that lower particles are less
//  able to be moved by particles above them. Press alt to render the shock directions.
//
//      *mouse to zoom*
//      *alt to render shock direction field*
//      *space to reset*
//
//  Buffer A, B, and C perform particle simulation. Only A updates the particle neighbors.
//  Buffer D computes screen-space voronoi for render.
//
// ---------------------------------------------------------------------------------------

void renderParticle(fxParticle v, vec2 p, uint id, float sc, inout vec4 O)
{
    float e = 2. * sc / R.y;
    float d = fxLinePointDist(v.pos, v.prev, p);

    vec3 color = v.color;
    if (keyDown(KEY_ALT))
        color *= sin(atan(abs(v.sd.y * .1), v.sd.x) + vec3(0, 11, 33));
    O.xyz = mix(O.rgb, color, smoothstep(v.size + e, v.size - e, d));
}

// Character rendering from Shader Text for beginners by PrzemyslawZaworski:
// https://www.shadertoy.com/view/4sBfRd
#define C(c) O += char(U,64+c) ; U.x-=.5

#define LF (U.y++)
#define CR (U.x = (( uv.x - position.x)*64.0/FontSize))
#define NL (CR,LF)

vec4 char(vec2 p, int c) 
{
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel2, p/16. + fract( vec2(c, 15-c/16) / 16. ), dFdx(p/16.),dFdy(p/16.) ).xxxx;
}

void mainImage(out vec4 O, vec2 u)
{
    computeMaxParticles();
    vec2 p = (u - .5 * R) / R.y;
    float sc = 1.;

    if (iMouse.z > 0.)
    {
        sc = .25; // mouse zoom
        p.xy *= .25;
        p.xy += .6 * (iMouse.xy - .5 * R) / R.y;
    }

    uvec4 old = fxGetClosest(ivec2(p * R.y + .5 * R.xy));
    fxState s = fxGetState();
    O.rgb = BACK_COLOR;

    // render particles
    if ((!s.init || s.frame < CAPTURE_FRAME) && s.frame > 1)
    {
        for (int i = 0; i < 4; ++i)
        {
            uint id = old[i];
            if (id == -1U || id >= g_MaxParticles) break;
            fxParticle v = fxGetParticle(id);
            renderParticle(v, p, id, sc, O);
        }
    }

    float d = sdScene(p, R);
    O = mix(O, vec4(1), smoothstep(0., -1. / R.y, d)); // render border

    if (s.init)
    {
        if (abs(p.x) < .4 && abs(p.y) < .1)
        {
            O *= .5; // render rectangle
            const float FontSize = 4.;
            const vec2 position = vec2(.31,.5 - FontSize / 128.0);
            vec2 U = ( (p * R.y + .5 * R) / R - position)*64.0/FontSize;
            C(16);C(12);C(5);C(1);C(19);C(5);C(-32);C(23);C(1);C(9);C(20); // "PLEASE WAIT"
        }

        if (p.y < -.4 && p.x < ((float(s.frame) / float(CAPTURE_FRAME) - .5)) * R.x / R.y)
            O *= .5; // progress bar
    }

    O.a = 1.;
}

