void mainImage( out vec4 O, vec2 fragCoord )
{
    uvec2 ufc = uvec2(fragCoord);

    computeMaxParticles();
    uint index = ufc.x + ufc.y * uint(R.x); // "1D array" index
    uint id = index / NUM_PARTICLE_DATA_TYPES; // which particle
    uint dataType = index - id * NUM_PARTICLE_DATA_TYPES; // which field of particle structure
    if (id >= g_MaxParticles) return;

    fxParticle p = fxGetParticle(id);
    fxState s = fxGetState();

    if (dataType >= POS)
        O = updateParticle(s, p, id, dataType);
    else
        O = voronoiParticle(s, p, id, dataType);
}

