        x = absmin(x,tri(p,d,o,_T1));
        x = absmin(x,tri(p,d,o,_T2));
        x = absmin(x,tri(p,d,o,_T3));
        x = absmin(x,tri(p,d,o,_T4));
        x = absmin(x,tri(p,d,o,_T5));
        x = absmin(x,tri(p,d,o,_T6));
        x = absmin(x,tri(p,d,o,_T7));
        x = absmin(x,tri(p,d,o,_T8));
        x = absmin(x,tri(p,d,o,_T9));
        x = absmin(x,tri(p,d,o,_T10));
        x = absmin(x,tri(p,d,o,_T11));
        x = absmin(x,tri(p,d,o,_T12));


    }

    vec3 q = p + d*x;
    vec3 b = vec3(0);
    for (int i = min(0,iFrame); i < N; i++) {
        obj o = get(i);
		vec3 r = (q-o.m)*o.io;
		b += B(r,vec3(1.01*_s_),vec3(0),vec3(1,0,0))*o.o;
    }

     Q =  (sin(-2.+length(b)*.25+vec4(1,2,3,4)));
     float s = abs(sin(b.x*5.));
    Q *= smoothstep(.0,1.*fwidth(s),s);
    vec3 no =normalize(vec3(R*vec2(dFdx(x),dFdy(x)),1));
    Q.xyz *= .7+.5*no.z;
    //Q = texture(iChannel0,floor((U)*.02)/R);
}

