#define N 7

#define dt .08

#define G 6e-4
#define K  3e-5
#define K1 .01
#define K2 .1
#define K3 .000

#define Main void mainImage(out vec4 Q, in vec2 U)
#define R iResolution.xy
#define A(x,y) texelFetch(iChannel0,ivec2(x,y),0)

#define _s_ vec3(.3,.2,.15)

#define _a _s_*vec3(-1, -1,  1)
#define _b _s_*vec3( 1, -1,  1)
#define _c _s_*vec3(-1,  1,  1)
#define _d _s_*vec3( 1,  1,  1)
#define _e _s_*vec3(-1, -1, -1)
#define _f _s_*vec3( 1, -1, -1)
#define _g _s_*vec3(-1,  1, -1)
#define _h _s_*vec3( 1,  1, -1)

// Define the 12 triangles
#define _T1  _a, _e, _b
#define _T2  _b, _e, _f
#define _T3  _b, _f, _d
#define _T4  _d, _f, _h
#define _T5  _d, _h, _c
#define _T6  _c, _h, _g
#define _T7  _c, _g, _a
#define _T8  _a, _g, _e
#define _T9  _c, _b, _d
#define _T10 _c, _a, _b
#define _T11 _g, _f, _h
#define _T12 _g, _f, _e



struct obj {
    vec3 m; // 0
    mat3 o; // 1 2 3
    vec3 v; // 4
    vec3 s; // 5

    mat3 io; 

    vec3 f; 
    vec3 t; 
};

mat3 matrix (vec3 r) { 
   if (length(r)==0.) return mat3(1);
   float a = length(r);
   vec3 o = normalize(r);
   float c = cos(a), t = 1.-c;
   float s = sin(a);
   return mat3(
       t*o.x*o.x+c,t*o.x*o.y-o.z*s,t*o.x*o.z+o.y*s,
       t*o.x*o.y+o.z*s,t*o.y*o.y+c,t*o.y*o.z-o.x*s,
       t*o.x*o.z-o.y*s,t*o.y*o.z+o.x*s,t*o.z*o.z+c

   );
}
float iSphere (vec3 p, vec3 d, vec3 m, float r) {

    p -= m;

    float b = 2.*dot(p,d);
    float c = dot(p,p)-r*r;
    float s = (b*b-4.*c);
    if (s < 0.) return 1e9;
    else return .5*(-b-sqrt(s));

}
float iSegment (vec3 p, vec3 d, vec3 a, vec3 b, float r) {    
    vec3 q = p-a;
    vec3 u = b-a;
    float du = dot(d,u);
    float uu = dot(u,u);
    float qu = dot(q,u);
    float dq = dot(d,q);
    float qq = dot(q,q);
    float a_ = 1. - du*du/uu;
    float b_ = 2.*(dq - qu*du/uu);
    float c_ = qq - qu*qu/uu - r*r;
    float s = b_*b_-4.*a_*c_;
    if (s < 0.) return 1e9;
    float i = (-b_-sqrt(s))/(2.*a_);
    float x = dot(p+d*i-a,u)/uu;
    if (x < 0. || x > 1.) return 1e9;
    return i;
}
float iPlane (vec3 p, vec3 d, vec3 n) {
    return -dot(p,n)/dot(d,n);
}
float iTriangle (vec3 p, vec3 d, vec3 a, vec3 b, vec3 c) {
    vec3 n = normalize(cross(b-a,c-a));
    float i = -dot(p-a,n)/dot(d,n);
    vec3 x = p + d*i;
    float ab = dot(x-a,cross(n,normalize(b-a)));
    float bc = dot(x-b,cross(n,normalize(c-b)));
    float ca = dot(x-c,cross(n,normalize(a-c)));
    if (ab<0.||bc<0.||ca<0.) return 1e9;
    return i;
}

float iTriple (vec3 p, vec3 d, vec3 a, vec3 b, vec3 c, float r1, float r2) {
       float i = 1e9;

     i = min(i,iSphere(p,d,a,r1));
     i = min(i,iSphere(p,d,b,r1));
     i = min(i,iSphere(p,d,c,r1));

     i = min(i,iSegment(p,d,a,b,r2));
     i = min(i,iSegment(p,d,b,c,r2));
     i = min(i,iSegment(p,d,c,a,r2));

     i = min(i,iTriangle(p,d,a,b,c));
     return i;
}


float dSegment (vec3 p, vec3 a, vec3 b) {
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
float dBox ( vec2 p, vec2 b, float r) 
		{
		    vec2 h = 0.5*vec2(b.x,b.y);
		    vec2 q = p-clamp(p,-h+r,h-r);
		    if (length(q)>0.) return length(q) - r ;
            else return max((abs(p.x)-h.x)*h.y,(abs(p.y)-h.y)*h.x);
		}
mat3 rotationMatrix(float a, float b, float c)
{
    float cosA = cos(a);
    float sinA = sin(a);
    float cosB = cos(b);
    float sinB = sin(b);
    float cosC = cos(c);
    float sinC = sin(c);

    mat3 rotX = mat3(
        1.0, 0.0, 0.0,
        0.0, cosA, -sinA,
        0.0, sinA, cosA
    );

    mat3 rotY = mat3(
        cosB, 0.0, sinB,
        0.0, 1.0, 0.0,
        -sinB, 0.0, cosB
    );

    mat3 rotZ = mat3(
        cosC, -sinC, 0.0,
        sinC, cosC, 0.0,
        0.0, 0.0, 1.0
    );

    // Combine the three rotation matrices
    return rotZ * rotY * rotX;
}
vec3 velocity (obj o, vec3 p) {
    return o.v+cross(o.s,p-o.m);
}
void impulse (inout obj o, vec3 p, vec3 f) {
    vec3 r = p-o.m;
    if (length(r)>0.) {
        o.f += f*dot(normalize(f),normalize(-r))+f*cross(normalize(r),o.s);
        o.t += cross(r,f)/(_s_);
    } else o.f += f;
}
bool line_triangle (vec3 a, vec3 b, vec3 e, vec3 g, vec3 h, inout vec3 p, inout vec3 f) {
    vec3 u = normalize(b-a);
    float i = iTriangle (a,u,e,g,h);
    if (i < 0. || i > length(a-b)) {
        p = vec3(0), f = vec3(0);
        return false;
    } else {
            p = a + u*i;
            vec3 k = u;
            if (length(p-b) > length(p-a)) k = -u;
            f = normalize(cross(g-e,h-e));
            f *= -K1*dot(k,f)*length(a-b);
            return true;
    }
}

vec3 integral (vec3 p, vec3 h, vec3 j, vec3 m) {
		    p -= h;
		    float L = length(p);
		    float X = p.x, Y = p.y, Z = p.z,
		          A = j.x, B = j.y, C = j.z;
		    vec3 bj = vec3(
		             B*(
		                 -sign(X)*abs(Z)*atan(-Y*abs(X)/L/abs(Z))
		                 +X/2.*(log(L-Y)-log(L+Y))
		                 +Y/2.*(log(L-X)-log(L+X))
		             ) + 
		             C*(
		                 -sign(X)*abs(Y)*atan(-Z*abs(X)/L/abs(Y))
		                 +X/2.*(log(L-Z)-log(L+Z))
		                 +Z/2.*(log(L-X)-log(L+X))
		             )
		           , 
		             C*(
		                 -sign(Y)*abs(X)*atan(-Z*abs(Y)/L/abs(X))
		                 +Y/2.*(log(L-Z)-log(L+Z))
		                 +Z/2.*(log(L-Y)-log(L+Y))
		             ) + 
		             A*(
		                 -sign(Y)*abs(Z)*atan(-X*abs(Y)/L/abs(Z))
		                 +Y/2.*(log(L-X)-log(L+X))
		                 +X/2.*(log(L-Y)-log(L+Y))
		             )
		           ,
		             A*(
		                 -sign(Z)*abs(Y)*atan(-X*abs(Z)/L/abs(Y))
		                 +Z/2.*(log(L-X)-log(L+X))
		                 +X/2.*(log(L-Z)-log(L+Z))
		             ) + 
		             B*(
		                 -sign(Z)*abs(X)*atan(-Y*abs(Z)/L/abs(X))
		                 +Z/2.*(log(L-Y)-log(L+Y))
		                 +Y/2.*(log(L-Z)-log(L+Z))
		             )
		         );

		    A = m.x;
		    B = m.y;
		    C = m.z;
		    vec3 bm = -vec3(
		            +A*sign(X)*sign(Y)*atan(-Z*abs(Y)/L/abs(X))
		            -B*log((L-Z)/sqrt(X*X+Y*Y))
		            -C*sign(Y)*atan(L/abs(Y))

		            +A*sign(X)*sign(Z)*atan(-Y*abs(Z)/L/abs(X))
		            -C*log((L-Y)/sqrt(X*X+Z*Z))
		            -B*sign(Z)*atan(L/abs(Z))
		          ,
		            +B*sign(Y)*sign(X)*atan(-Z*abs(X)/L/abs(Y))
		            -A*log((L-Z)/sqrt(X*X+Y*Y))
		            -C*sign(X)*atan(L/abs(X))

		            +B*sign(Y)*sign(Z)*atan(-X*abs(Z)/L/abs(Y))
		            -C*log((L-X)/sqrt(Z*Z+Y*Y))
		            -A*sign(Z)*atan(L/abs(Z))
		          ,
		            +C*sign(Z)*sign(Y)*atan(-X*abs(Y)/L/abs(Z))
		            -B*log((L-X)/sqrt(Z*Z+Y*Y))
		            -A*sign(Y)*atan(L/abs(Y))

		            +C*sign(Z)*sign(X)*atan(-Y*abs(X)/L/abs(Z))
		            -A*log((L-Y)/sqrt(Z*Z+X*X))
		            -B*sign(X)*atan(L/abs(X))
		    );

		    return bj+bm;

		}



		vec3 B (vec3 p, vec3 h, vec3 j, vec3 m) {
		    return (
		      (
		        ( integral(p,vec3(+h.x,+h.y,+h.z),j,m) - integral(p,vec3(-h.x,+h.y,+h.z),j,m) ) 

		            -

		        ( integral(p,vec3(+h.x,-h.y,+h.z),j,m) - integral(p,vec3(-h.x,-h.y,+h.z),j,m) ) 
		      ) 

		          - 

		      (
		        ( integral(p,vec3(+h.x,+h.y,-h.z),j,m) - integral(p,vec3(-h.x,+h.y,-h.z),j,m) ) 

		            -

		        ( integral(p,vec3(+h.x,-h.y,-h.z),j,m) - integral(p,vec3(-h.x,-h.y,-h.z),j,m) ) 
		      )
		    );
		}

