void tri_boundaries (inout obj o, vec3 a, vec3 b, vec3 c) {
    a = a*o.o+o.m;
    b = b*o.o+o.m;
    c = c*o.o+o.m;
    vec3 da = -velocity(o,a);
    vec3 db = -velocity(o,b);
    vec3 dc = -velocity(o,c);

    if (a.y < -.5) impulse(o, a, vec3(0,-a.y-.5,0) + K3*da);
    if (b.y < -.5) impulse(o, b, vec3(0,-b.y-.5,0) + K3*db);
    if (c.y < -.5) impulse(o, c, vec3(0,-c.y-.5,0) + K3*dc);

    if (a.x < -1.) impulse(o, a, vec3(-a.x-1.,0,0) + K3*da);
    if (b.x < -1.) impulse(o, b, vec3(-b.x-1.,0,0) + K3*db);
    if (c.x < -1.) impulse(o, c, vec3(-c.x-1.,0,0) + K3*dc);

    if (a.z < -1.) impulse(o, a, vec3(0,0,-a.z-1.) + K3*da);
    if (b.z < -1.) impulse(o, b, vec3(0,0,-b.z-1.) + K3*db);
    if (c.z < -1.) impulse(o, c, vec3(0,0,-c.z-1.) + K3*dc);

    if (a.x > +1.) impulse(o, a, vec3(1.-a.x,0,0) +  K3*da);
    if (b.x > +1.) impulse(o, b, vec3(1.-b.x,0,0) +  K3*db);
    if (c.x > +1.) impulse(o, c, vec3(1.-c.x,0,0) +  K3*dc);

    if (a.z > +1.) impulse(o, a, vec3(0,0,1.-a.z) +  K3*da);
    if (b.z > +1.) impulse(o, b, vec3(0,0,1.-b.z) +  K3*db);
    if (c.z > +1.) impulse(o, c, vec3(0,0,1.-c.z) +  K3*dc);

}
