// Heart function
float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot(p-vec2(0.25,0.75), p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot(p-vec2(0.00,1.00), p-vec2(0.00,1.00)),
                    dot(p-0.5*max(p.x+p.y,0.0), p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

// Palette function
vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

// Main function
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {
        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        // Modify distance using sdHeart
        d += sdHeart(uv) * 0.1;

        vec3 col = palette(length(uv0) + i * 0.4 + iTime * 0.4);

        d = sin(d * 8.0 + iTime) / 8.0;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}

float normSin(float x) {
    return (sin(x) + 1.)/2.;
}

vec4 fadeColor(float i, float t) {
    float rFactor = 1.0;
    float gFactor = 1.4;
    float bFactor = 1.3;
    return vec4(i*normSin(rFactor*t), i*normSin(gFactor*t), i*normSin(bFactor*t), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float offSet = 10000.;
    float speed = 0.03;
    float density = 0.01;
    float dateOffset = mix(-5000., 5000., (iDate.z + iDate.w/86400.) / 31.);
    offSet += dateOffset;

    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    float d = length(uv);

