#define TIME        iTime
#define RESOLUTION  iResolution

#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define OFF6(n)     (vec2(1.0, 0.0)*ROT(n*tau/6.))
#define SAT(x)      clamp(x,0.,1.)

//#define APPROX_MATH

const float
  pi        = acos(-1.)
, tau       = 2.*pi
, pi_2      = 0.5*pi
, planeDist = .5
, furthest  = 6.
, fadeFrom  = 4.
;
const vec3  
  L  = vec3(0.299, 0.587, 0.114)
, LD = normalize(vec3(0.0, 1.8, 1.0))
