vec2 march(const in vec3 ro, const in vec3 rd, const in vec3 lDir, out vec3 res)
{

    const uint marchSteps = uint(512),
               shadowSteps = uint(4);

    float d = 0.0, t = 0.0;
    float stepSize = 1. / float(marchSteps);
    //float sigmaT = 1.0;
    //float transparency = 1.0;

    float stepL = 1. / float(shadowSteps);

    vec3 lD = lDir * stepL;
    vec3 rD = rd * stepSize;

    res = vec3(0.);

    //vec3 ligC = vec3(0.600,0.678,0.529);

    float curDensity = 0.;
    float transmittance = 1.;
    vec3 lightEnergy = vec3(0.);

    //float accumDist = 0.;

    vec3 bac = mix(vec3(0.996,0.867,0.584), vec3(0.408,0.596,0.973), rd.z+0.5);

    for (uint i = 0u; i < marchSteps; ++i)
    {

        #if (A1 == 0)
        vec3 p = ro + rd * t;
        d = map(p);

        if (d < 0.001)
        {
             float sha = 0.0;

             vec3 pL = p;

             float lT = 0.;

             for (uint j = 0u; j < shadowSteps; ++j)
             {
                 pL = pL + lDir * lT;
                 float lD = map(pL);
                 sha += lD;

                 lT += stepL;
             }

            curDensity = (d*-1.);
            float shadowTerm = exp(-sha * stepL);
            float absorbedLight = clamp(shadowTerm * curDensity * 0.95, 0., 1.);
            lightEnergy += vec3(1.000,0.678,0.655) * absorbedLight * transmittance;// * stepSize;
            transmittance *= 1.-curDensity;
        }
        if (transmittance < 1e-3)
        {
            break;
        }

        t += stepSize; 

        #else
        if (transparency < 1e-3)
        {
            break;
        }
        vec3 p = ro + rd * t;
        d = map(p);

        if (d <= 0.0)
        {

            float sample_attenuation = exp(-stepSize * d * sigmaT);
            transparency *= sample_attenuation;

            float lT = 0.0;
            float tau = 0.0;
            float tt = 0.;
            for (uint j = 0u; j < shadowSteps; ++j)
            {
                vec3 lP = p + lDir * tt;
                tau += map(lP);
                tt += stepL;
            }
            float lightRayAtt = exp(-tau * stepL);

            res += lightRayAtt * transparency * stepSize;

        }
        t += stepSize;
        #endif
    }
    #if (A1 == 0)
    res = lightEnergy;
    #endif
    //res = mix(res, bac*(1.-transmittance), lightEnergy);
    res += bac * transmittance;

    //res = bac * transmittance + res;

    return vec2(t, d);
}

