float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (-iResolution.xy + fragCoord * 2.0) / iResolution.y;

    vec2 mou = iMouse.xy / iResolution.xy;

    vec3 ro = vec3(0.0, 0.1, -1.5);
    vec3 rd = normalize(vec3(uv, 1.0));

    #if 1

    mat2 r = rot(mou.y*-PI*.05);

    //ro.z += iTime *.2;
    //rd.z += iTime *.2;

    ro.yz *= r;
    rd.yz *= r;

    r = rot(mou.x*TWO_PI);

    ro.xy *= r;
    rd.xy *= r;



    #endif



    vec2 recRes = 1./iResolution.xy;
    float mRecRes = max(recRes.x, recRes.y);

    float rat = iResolution.x/iResolution.y;

    float v = sdRoundedBox( uv, vec2(.87, 0.8)*vec2(rat, 1.), vec4(0.));

    // Time varying pixel color
    vec3 col0 = mix(vec3(1), vec3(0), smoothstep(0., mRecRes*150., v));

    vec3 col = shade(ro, rd);

    // Output to screen

    fragColor = mix(vec4(col, 1.), vec4(0.), vec4(1.-col0, 1.));
    //fragColor = vec4(pow(col, vec3(1.)),1.0);
}

