vec4 achnoise(float x){
	float p = floor(x);
	float fr = fract(x);
	float L = p;
	float R = p + 1.0;

	vec4 Lo = oct(L);
	vec4 Ro = oct(R);

	return mix(Lo, Ro, fr);
} 

vec4 achnoise(vec2 x){
	vec2 p = floor(x);
	vec2 fr = fract(x);
	vec2 LB = p;
	vec2 LT = p + vec2(0.0, 1.0);
	vec2 RB = p + vec2(1.0, 0.0);
	vec2 RT = p + vec2(1.0, 1.0);

	vec4 LBo = oct(LB);
	vec4 RBo = oct(RB);
	vec4 LTo = oct(LT);
	vec4 RTo = oct(RT);

	vec4 noise1d1 = mix(LBo, RBo, fr.x);
	vec4 noise1d2 = mix(LTo, RTo, fr.x);

	vec4 noise2d = mix(noise1d1, noise1d2, fr.y);

	return noise2d;
} 
