float noise(vec2 p, float t)
{
    vec4 b = vec4(floor(p), ceil(p));
    vec2 f = smoothstep(0.0, 1.0, fract(p));
    return mix(mix(hash(b.xy, t), hash(b.zy, t), f.x), mix(hash(b.xw, t), hash(b.zw, t), f.x), f.y);
}
#define num_octaves 1
float fbm(vec2 v)
{
    float value = 0.0;
    float scale = 1.0;
    float atten = 0.5;
    float t = 0.0;
    for(int i = 0; i < num_octaves; i++)
    {
        t += atten;
        value += noise(v*scale, float(i))*atten;
        scale *= 2.0;
        atten *= 0.5;
        v = rotate(v, 0.125*pi);
    }
    return value/t;
}
vec2 fbm_vec(vec2 v, vec2 offset)
{
    return vec2(fbm(v), fbm(v + offset));
}
vec2 warp(vec2 v, float str, vec2 offset)
{
    return v + fbm_vec(v, offset) * str;
}
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
