//=======================================================================================
vec3 SphericalToCartesian (float theta, float phi, float radius)
{    
    return vec3(
        sin(phi) * cos(theta) * radius,
        sin(phi) * sin(theta) * radius,
        cos(phi) * radius
    );
}

//=======================================================================================
vec3 CartesianToSpherical (vec3 p)
{   
    float radius = length(p);
    float theta = atan(p.y, p.x);
    float phi = atan(length(p.xy), p.z);
    return vec3(theta, phi, radius);
}


//=======================================================================================
float QuadraticBezier (float A, float B, float C, float t)
{
    float s = 1.0 - t;
    float s2 = s * s;
    float t2 = t * t;

    return A*s2 + B*2.0*s*t + C*t2;
}


//=======================================================================================
float DistanceAtPos(vec3 P)
{
    vec3 spherical = CartesianToSpherical(P);

	float theta = spherical.x;  //-pi, +pi
    float phi = spherical.y;    //-pi, +pi
    float radius = spherical.z;

    // normalize each angle to a [0,1] value
    theta = theta / (c_twoPi);
    if (theta < 0.0)
        theta += 1.0;  

    phi = phi / (c_twoPi);
    if (phi < 0.0)
        phi += 1.0;

    /*
    if (theta > 0.45)
        return 0.8;
    else
        return 0.5;
    */

    // make the bezier surface
    float CP0X = QuadraticBezier(CP00, CP01, CP02, theta);
    float CP1X = QuadraticBezier(CP10, CP11, CP12, theta);
    float CP2X = QuadraticBezier(CP20, CP21, CP22, theta);

    return QuadraticBezier(CP0X, CP1X,CP2X, phi);
}

