//=======================================================================================
bool RayIntersectAABox (vec3 boxMin, vec3 boxMax, in vec3 rayPos, in vec3 rayDir, out vec2 time)
{
	vec3 roo = rayPos - (boxMin+boxMax)*0.5;
    vec3 rad = (boxMax - boxMin)*0.5;

    vec3 m = 1.0/rayDir;
    vec3 n = m*roo;
    vec3 k = abs(m)*rad;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

    time = vec2( max( max( t1.x, t1.y ), t1.z ),
                 min( min( t2.x, t2.y ), t2.z ) );

    return time.y>time.x && time.y>0.0;
}

//=======================================================================================
bool RayIntersectSphere (vec4 sphere, in vec3 rayPos, in vec3 rayDir, out vec2 time)
{
	//get the vector from the center of this circle to where the ray begins.
	vec3 m = rayPos - sphere.xyz;

    //get the dot product of the above vector and the ray's vector
	float b = dot(m, rayDir);

	float c = dot(m, m) - sphere.w * sphere.w;

	//exit if r's origin outside s (c > 0) and r pointing away from s (b > 0)
	if(c > 0.0 && b > 0.0)
		return false;

	//calculate discriminant
	float discr = b * b - c;

	//a negative discriminant corresponds to ray missing sphere
	if(discr < 0.0)
		return false;

	//ray now found to intersect sphere, find and set the two collision times
	float collisionTime1 = -b - sqrt(discr);
	float collisionTime2 = -b + sqrt(discr);

    time = vec2( min(collisionTime1, collisionTime2),
                 max(collisionTime1, collisionTime2));

    return time.y>time.x && time.y>0.0;
}

//=======================================================================================
vec3 DiffuseColor (in vec3 pos)
{
    float radius = length(pos);
	float theta = atan(pos.y, pos.x);
    float phi = acos(pos.z / radius);   

    // normalize each angle to a [0,1] value
    theta = theta / (c_twoPi) + 0.5;
    phi = phi / c_pi;    

	bool whiteSquare = true;

    if (mod(theta*26.0, 2.0) < 1.0)
		whiteSquare = !whiteSquare;

	if (mod(phi*26.0, 2.0) < 1.0)
		whiteSquare = !whiteSquare;	

    return vec3(whiteSquare ? 1.0 : 0.4);
}

//=======================================================================================
vec3 ShadePoint (in vec3 pos, in vec3 rayDir, float time)
{
	vec3 diffuseColor = DiffuseColor(pos);
	vec3 reverseLightDir = normalize(vec3(1.0,1.0,-1.0));
	vec3 lightColor = vec3(0.95,0.95,0.95);	
	vec3 ambientColor = vec3(0.05,0.05,0.05);

	vec3 normal = NormalAtPos(pos);

    // diffuse
	vec3 color = diffuseColor * ambientColor;
	float dp = dot(normal, reverseLightDir);
	if(dp > 0.0)
		color += (diffuseColor * dp * lightColor);

    // specular
    vec3 reflection = reflect(reverseLightDir, normal);
    dp = dot(rayDir, reflection);
    if (dp > 0.0)
        color += pow(dp, 15.0) * vec3(0.5);	

    // reflection (environment mappping)
    reflection = reflect(rayDir, normal);
    color += texture(iChannel0, reflection).rgb * 0.25;

    return color;
}

//=======================================================================================
vec3 HandleRay (in vec3 rayPos, in vec3 rayDir, in vec3 pixelColor, out float hitTime)
{
	float time = 0.0;
	float lastHeight = 0.0;
	float lastY = 0.0;
	float height;
	bool hitFound = false;
    hitTime = FLT_MAX;

    vec2 timeMinMax = vec2(0.0);
    if (!RayIntersectSphere(vec4(0.0,0.0,0.0,1.0), rayPos, rayDir, timeMinMax))
        return pixelColor;

    time = timeMinMax.x;

    const int c_numIters = 100;
    float deltaT = (timeMinMax.y - timeMinMax.x) / float(c_numIters);

    vec3 pos = rayPos + rayDir * time;
	for (int index = 0; index < c_numIters; ++index)
	{		
		pos = rayPos + rayDir * time;

        height = DistanceAtPos(pos);

        float actualDist = length(pos);

        if (actualDist < height)
        {
        	hitFound = true;
			break;
        }

		time += deltaT;		
		lastHeight = height;
		lastY = pos.y;
	}

	if (hitFound) {
		//time = time - deltaT + deltaT*(lastHeight-lastY)/(pos.y-lastY-height+lastHeight);
		//pos = rayPos + rayDir * time;
		pixelColor = ShadePoint(pos, rayDir, time);
        hitTime = time;
	}
    else {
        pixelColor += vec3(0.2);
    }

	return pixelColor;
}

