    if (RayIntersectSphere(vec4(SphericalToCartesian(c_fullAngle / 2.0, c_fullAngle / 4.0, CP11), 0.01), rayPos, rayDir, cpHitTime) && cpHitTime.x < hitTime)
    {
        hitTime = cpHitTime.x;
        pixelColor = vec3(0.0,1.0,0.0);
    }    
    if (RayIntersectSphere(vec4(SphericalToCartesian(c_fullAngle, c_fullAngle / 4.0, CP12), 0.01), rayPos, rayDir, cpHitTime) && cpHitTime.x < hitTime)
    {
        hitTime = cpHitTime.x;
        pixelColor = vec3(0.0,1.0,0.0);
    }     


    if (RayIntersectSphere(vec4(SphericalToCartesian(0.0, c_fullAngle / 2.0, CP20), 0.01), rayPos, rayDir, cpHitTime) && cpHitTime.x < hitTime)
    {
        hitTime = cpHitTime.x;
        pixelColor = vec3(0.0,0.0,1.0);
    }
    if (RayIntersectSphere(vec4(SphericalToCartesian(c_fullAngle / 2.0, c_fullAngle / 2.0, CP21), 0.01), rayPos, rayDir, cpHitTime) && cpHitTime.x < hitTime)
    {
