            vec2 gv = vec2(
            uv.x * 0.7 + hash(uv + fract(iTime) * e) * i,
            uv.y * 0.7 + hash(uv + shape + fract(iTime) * e) * j
            );

            float d = length(gv);
            vec3 col = vec3(0.01 / d);
