            d1 = sign_side(pt, v1, v2);
            d2 = sign_side(pt, v2, v3);
            d3 = sign_side(pt, v3, v1);

            has_neg = (d1 < 0.) || (d2 < 0.) || (d3 < 0.);
            has_pos = (d1 > 0.) || (d2 > 0.) || (d3 > 0.);

            return float((has_neg && has_pos))*2.-1.;
        }
        float triangle (vec2 p, vec2 a, vec2 b, vec2 c) {
            return PointInTriangle(p.xy,a,b,c)*min(segment(p,a,b),min(segment(p,b,c),segment(p,c,a)));
        }
        float triPrism(vec3 p, vec2 a, vec2 b, vec2 c, float h) {
          vec2 d = vec2(triangle(p.xy,a,b,c),abs(p.z)-.5*h);

          return min(max(d.x,d.y),0.0) + length(max(d,0.0));
        }
		float triPrism( vec3 p, vec2 h )
        {
          vec3 q = abs(p);
          return max(q.z-.5*h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
        }
        float segment ( vec3 p, vec3 a, vec3 b, float r )
        {
          vec3 pa = p - a, ba = b - a;
          float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
          return length( pa - ba*h ) - r;
        }
		float box(vec2 p, vec2 b) {
			vec2 d = abs(p) - b*0.5;
		    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
		}
		float box( vec3 p, vec3 b )
		{
		    vec3 d = abs(p) - b*0.5;
		    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
		}
		float box(vec3 p, float w, float h, float l) {return box(p,vec3(w,h,l));}
		float box(vec2 p, float w, float h) {return box(p,vec2(w,h));}
		float roundBox ( vec3 p, vec3 b, float r) 
		{
		    vec2 h = 0.5*vec2(b.x,b.y)-r;
		    p.xy = abs(p.xy);
		    p.xy = p.xy-clamp(p.xy,-h,h);
		    return cylinder(p,vec2(r,b.z));
		}
		float roundBox ( vec2 p, vec2 b, float r) 
		{
		    vec2 h = 0.5*vec2(b.x,b.y)-r;
		    p.xy = abs(p.xy);
		    p.xy = p.xy-clamp(p.xy,-h,h);
		    return length(p)-r;
		}
		float roundBox(vec3 p, float w, float h, float l, float r) {return roundBox(p,vec3(w,h,l),r);}
		float roundBox(vec2 p, float w, float h, float r) {return roundBox(p,vec2(w,h),r);}






float wheels (vec3 p, float s) {
    vec3 q = p;
    p = abs(p)-vec3(.3,0,.3);
    s *= 2.*sign(-q.z);

    if (q.x<0.) p.xz *= mat2(cos(s),sin(s),-sin(s),cos(s));
    return max(cylinder(p,.1,.1),-cylinder(p-vec3(0,.0,0),.07,.15));
}
float map (vec3 p, float time) {
    float d = 1e9;
    float s = .1*sin(.2*time);
    p.z += s;
    p.y += 1.;
    p *= .8;
    p.xz *= mat2(cos(s),sin(s),-sin(s),cos(s));
    float w = wheels(p-vec3(0,.2,0),.2*cos(.2*time));
    d = min(d,roundBox(p-vec3(0,.3,0),vec3(1.,.2,.6),.02));
    d = min(d,triPrism(p,vec2(-.3,.4),vec2(-.1,.4),vec2(-.1,.6),.45));
    d = min(d,roundBox(p-vec3(.02,.5,0),vec3(.25,.2,.45),.02));
    d = max(d,min(w-.06,-roundBox(p-vec3(.3,.4,0),vec3(.3,.14,.45),.02)));
    d = min(max(d,-w+.05),w);
    p.z = abs(p.z);
    d = min(d,roundBox(p.zyx-vec3(.3,.44,-.25),vec3(.1,.05,.01),.01));
    return d/.8-.014;
}

