int face (vec3 dir){
    if        (dir.x>max(abs(dir.y),abs(dir.z))){
        return 0;
    } else if (dir.y>max(abs(dir.z),abs(dir.x))){
        return 1;
    } else if (dir.z>max(abs(dir.x),abs(dir.y))){
        return 2;
    } else if (-dir.x>max(abs(dir.y),abs(dir.z))){
        return 3;
    } else if (-dir.y>max(abs(dir.z),abs(dir.x))){
