    return H(_3D(V),i); 
}
vec4 T (vec3 u, int i) {
    u -= .5;
    vec3 f = floor(u);
    vec3 c = ceil(u);
    vec4 _000 = G(f,i);
    vec4 _001 = G(vec3(f.xy,c.z),i);
    vec4 _010 = G(vec3(f.x,c.y,f.z),i);
    vec4 _011 = G(vec3(f.x,c.yz),i);
    vec4 _100 = G(vec3(c.x,f.yz),i);
    vec4 _101 = G(vec3(c.x,f.y,c.z),i);
    vec4 _110 = G(vec3(c.xy,f.z),i);
    vec4 _111 = G(c,i);
    vec4 _00 = mix(_000,_001,fract(u.z));
    vec4 _01 = mix(_010,_011,fract(u.z));
    vec4 _10 = mix(_100,_101,fract(u.z));
    vec4 _11 = mix(_110,_111,fract(u.z));
    vec4 _0 = mix(_00,_01,fract(u.y));
    vec4 _1 = mix(_10,_11,fract(u.y));
    return mix(_0,_1,fract(u.x));
}
vec4 F (vec3 u, int i) {
    if (iFrame%4==0) {
        u -= .5*G(u,0).xyz;
        u -= .5*T(u,0).xyz;
        u -= .5*G(u,0).xyz;
        u -= .5*T(u,0).xyz;
        return T(u,i);
    } else {
        return G(u,i);
    }
}
