    if (f < 2) {
        vec3 V = _3D(U)+R3.x*vec3(f,0,0);
        Q = F(V,0);
        float k = 1./6.;
        vec4
            x_ = F(V+vec3(-1,0,0),0),
            _x = F(V+vec3(+1,0,0),0),
            y_ = F(V+vec3(0,-1,0),0),
            _y = F(V+vec3(0,+1,0),0),
            z_ = F(V+vec3(0,0,-1),0),
            _z = F(V+vec3(0,0,+1),0);
        if (iFrame%2==0)
            Q.w -= .1*(Q.w-k*(_x.w+x_.w+_y.w-y_.w+_z.w-z_.w))+.5*(_x.x-x_.x+_y.y-y_.y+_z.z-z_.z) ;
