//=======================================================================================//
//
// Simple Sphere projection
// by Julien Sulpis (https://twitter.com/jsulpis)      
// https://www.shadertoy.com/view/DdtcWB
//
//=======================================================================================//


// Sphere params
#define SPHERE_POSITION vec3(0, 1, 6)
#define SPHERE_RADIUS 2.
#define ROTATION_SPEED .3
#define INFINITY 1e10
#define PI acos(-1.)

//===============================================//
//  Generic utilities stolen from smarter people //
//===============================================//

// https://iquilezles.org/articles/intersectors/
// sphere of size ra centered at point ce
float sphIntersect(in vec3 ro, in vec3 rd, in vec3 ce, float ra) {
  vec3 oc = ro - ce;
  float b = dot(oc, rd);
  float c = dot(oc, oc) - ra * ra;
  float h = b * b - c;
  if(h < 0.0)
    return INFINITY; // no intersection
  return -b - sqrt(h);
}

// Inigo Quilez - https://www.shadertoy.com/view/4djSDy
float sphOcclusion(in vec3 pos, in vec3 nor, in vec3 origin, in float radius) {
  vec3 di = origin - pos;
  float l = length(di);
  float nl = dot(nor, di / l);
  float h = l / radius;
  float h2 = h * h;
  float k2 = 1.0 - h2 * nl * nl;

  // above/below horizon
  // EXACT: Quilez - https://iquilezles.org/articles/sphereao
  float res = max(0.0, nl) / h2;

  // intersecting horizon 
  if(k2 > 0.001) {
    // EXACT : Lagarde/de Rousiers - https://seblagarde.files.wordpress.com/2015/07/course_notes_moving_frostbite_to_pbr_v32.pdf
    res = nl * acos(-nl * sqrt((h2 - 1.0) / (1.0 - nl * nl))) - sqrt(k2 * (h2 - 1.0));
    res = res / h2 + atan(sqrt(k2 / (h2 - 1.0)));
    res /= 3.141593;
  }

  return res;
}

mat3 rotateY(float angle) {
  float c = cos(angle);
  float s = sin(angle);
  return mat3(
      vec3(c, 0, s),
      vec3(0, 1, 0),
      vec3(-s, 0, c)
  );
}

//================//
//  Project code  //
//================//

struct Hit {
  float dist;
  vec3 color;
  vec3 normal;
};

vec2 sphereProjection(vec3 p, vec3 origin) {
  vec3 dir = normalize(p - origin);
  float longitude = atan(dir.x, dir.z); // ]-PI, PI]
  float latitude = asin(dir.y); // [-PI/2, PI/2]

  return vec2(
    (longitude + PI) / (2. * PI), // ]0, 1]
    (latitude + PI / 2.) / PI // [0, 1]
  );
}

float sphereDist(in vec3 ro, in vec3 rd, out vec2 textureCoord) {
  float simpleSphereDist = sphIntersect(ro, rd, SPHERE_POSITION, SPHERE_RADIUS);
  vec3 intersection = ro + simpleSphereDist * rd;
  vec3 intersectionWithRotation = (intersection - SPHERE_POSITION) * rotateY(iTime * ROTATION_SPEED) + SPHERE_POSITION;

  textureCoord = sphereProjection(intersectionWithRotation, SPHERE_POSITION); 

  float bump = length(vec3(texture(iChannel0, textureCoord))) * .02;
  float dist = sphIntersect(ro, rd, SPHERE_POSITION, SPHERE_RADIUS + bump);

  return dist;
}

float sphereDist(in vec3 ro, in vec3 rd) {
  vec2 tmp = vec2(0.);
  return sphereDist(ro, rd, tmp);
}

vec3 getNormal(vec3 p) {
  vec3 rd = SPHERE_POSITION - p;
  float dist = sphereDist(p, rd);
  vec2 e = vec2(.001, 0);

  vec3 normal = dist - vec3(
      sphereDist(p - e.xyy, rd), 
      sphereDist(p - e.yxy, rd), 
      sphereDist(p + e.yyx, rd)
  );
  return normalize(normal);
}

float checker(vec2 coord) {
  float size = 6.;
  float verticalStripes = step(fract(size * 2. * coord.x), 0.5) - 0.5;
  float horizontalStripes = step(fract(size * coord.y), 0.5) - 0.5;
  return step(verticalStripes * horizontalStripes, 0.0);
}

Hit interspectObject(in vec3 ro, in vec3 rd) {
  vec2 textureCoord = vec2(0.);
  float dist = sphereDist(ro, rd, textureCoord);
  vec3 intersection = ro + dist * rd;

  vec3 normal = getNormal(intersection);
  vec3 color = vec3(texture(iChannel0, textureCoord));
  //color = vec3(checker(textureCoord) + .03);

  float sphereAO = sphOcclusion(intersection, normal, SPHERE_POSITION, SPHERE_RADIUS);
  color *= vec3(pow(sphereAO, 1.5));

  return Hit(dist, color, normal);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord / iResolution.xy - 0.5) * iResolution.xy / iResolution.y;

  vec3 ro = vec3(0, 1, 0);
  vec3 rd = normalize(vec3(uv, 1));

  // background
  vec3 color = vec3(0.01);

  // sphere
  Hit hit = interspectObject(ro, rd);

  color = mix(hit.color, color, step(INFINITY, hit.dist));

  // gamma
  color = pow(color, vec3(1.0 / 2.2));

  fragColor = vec4(color, 1.0);
}

/* See *configuration* in Common for playing around. */

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    resolution = iResolution;
    vec4 root = texelFetch(iChannel0, ivec2(0), 0);
    float alpha = clamp(root.x / float(nstep), 0., 1.);
    vec4 tracks = texelFetch(iChannel1, ivec2(fragCoord), 0);
    vec4 stats = texelFetch(iChannel2, ivec2(fragCoord), 0);
    fragColor = vec4(1);
    //fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0)/20.+.5; return;
    //fragColor = tracks; return;
    //fragColor = stats/float(targets.length()-1); return;

    // target of a source point (scatterplot, one color per target)
    float hasTarget = float(stats.y >= 0.);
    blend(fragColor, vec4(hue(stats.y/float(targets.length()-1)),1), hasTarget);

    // forward tracks (black lines)
    blend(fragColor, vec4(0,0,0,1), (1. - tracks.x) * mix(1., .8, hasTarget));

    // source distribution (red dot, red circle)
    float r = length(fragCoord - world(vec2(0)));
    blend(fragColor, vec4(1,0,0,1), 3. - r);
    blend(fragColor, vec4(1,0,0,1), 1. - abs(3. * f() - r));

    // forward track heads (green dots, pink lines)
    for (int i=0 ; i<int(iResolution.x) ; ++i)
    {
        vec4 x = texelFetch(iChannel0, ivec2(i,1), 0);
        vec2 x1 = predict_x1(x, alpha, method);
        blend(fragColor, vec4(1,0,1,0.2), 1. - sdSegment(fragCoord, world(x.xy), world(x1)));
        blend(fragColor, vec4(0,.5,0,1), 2.5 - length(fragCoord - world(x.xy)));
    }

    // mean destination (orange dot)
    blend(fragColor, vec4(1,.2,0,1), 3. - length(fragCoord - world(mean_target())));

    // expected source for each target (purple circles)
    vec2 X0 = vec2(0);
    for (int j=0 ; j<targets.length() ; ++j)
    {
        vec2 mean_x0 = texelFetch(iChannel2, ivec2(j+1,0), 0).zw;
        blend(fragColor, vec4(0.5,0,1,1), 2. - abs(5. - length(fragCoord - world(mean_x0))));
        X0 += mean_x0;
    }
    X0 /= float(targets.length());

    // mean of expected sources, should be zero (orange circle)
    blend(fragColor, vec4(1,.2,0,1), 2. - abs(5. - length(fragCoord - world(X0))));

    // reverse tracks (green lines)
    blend(fragColor, vec4(0,.2,0,1), 1. - tracks.y);

    // reverse track heads (green dots)
    for (int j=0 ; j<targets.length() ; ++j)
    {
        vec4 x = texelFetch(iChannel0, ivec2(j+1,0), 0);
        //vec2 x0 = predict_x0(x, alpha, method);
        //blend(fragColor, vec4(1,0,1,1), 1.5 - sdSegment(fragCoord, world(x.xy), world(x0)));
        blend(fragColor, vec4(0,.5,0,1), 2.5 - length(fragCoord - world(x.xy)));
    }

    // targets (blue dots)
    for (int i=0 ; i<targets.length() ; ++i)
        blend(fragColor, vec4(0,.2,1,1), 3. - length(fragCoord - world(target(i))));

    fragColor = sqrt(fragColor);
}

