void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    ivec2 index = ivec2(fragCoord);
    vec4 x = rand(uvec4(iFrame * 0, fragCoord, 0));
    vec4 n = boxMuller(x);

    if (index == ivec2(0))
        // (0, 0) simulation progress
        fragColor = vec4(0, 0, 0, 0);
    else if (index.y == 0)
        // (x, 0) backward particles
        fragColor = target(index.x - 1).xyxy;
    else
        // forward particles
        fragColor = n.xyxy;

    if (iFrame == 0 || iMouse.w > 0.)
        return;

    vec4 root = texelFetch(iChannel0, ivec2(0), 0);
    float da = 1. / float(nstep);

    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    if (index == ivec2(0))
    {
        fragColor.x += 1.;
    }
    else if (index.y == 0)
    {
        fragColor = fragColor.xyxy;

        float alpha = 1. - root.x / float(nstep);
        if (alpha == 1.)
            fragColor.xy = undo_last(fragColor.xy, da, method);
        else if (alpha > 0.)
            fragColor.xy -= da * tangent(fragColor.xy, alpha, method);
    }
    else// if (index.y < 100)
    {
        fragColor = fragColor.xyxy;
        //fragColor.xy += n.xy * 0.01; return;

        float alpha = root.x / float(nstep);
        if (alpha < 1.)
            fragColor.xy += da * tangent(fragColor.xy, alpha, method);
     }
}

