float dot2(vec2 x) { return dot(x, x); }
#define R(a) mat2(cos(a), sin(a), -sin(a), cos(a))
const float pi = radians(180.);

/* configuration */

const int method = 0;  // 0: mix, 1: turn
const int nstep = 100; // deblending substeps

/*
Uniform scaling only changes the intermediate trajectories
and at which alpha the particles basically travel linearly
towards the targets!
Translation adds a constant velocity to the particles.
*/
// transform (skew, rotation, scale) applied to selected points
#define T mat2(1,0,0,1)*R(0.)/1.

const vec2 radius = vec2(5);    // view paddng
const vec2 offset = vec2(0, 0); // translation of targets

#if 1
// explicit target datasets
const vec2 targets[] = vec2[](
    //vec2(1, 0), vec2(0, 1)
    //vec2(2, 0), vec2(0, 2)
    //vec2(5, 0), vec2(0, 5)
    //vec2(.5, -.5), vec2(-.5, .5)
    T*vec2(5, 0), T*vec2(0, 5), T*vec2(-8, -3)//, T*vec2(-2, 0)
    //T*vec2(-8, 0), T*vec2(3, 0), T*vec2(5, 0)
    //vec2(0, -5), vec2(0, 2), vec2(0, 3)
    //vec2(-5,0), vec2(-4,0), vec2(-3,-1), vec2(-2,0), vec2(-1,0), vec2(0), vec2(1,0), vec2(2,0), vec2(3,0), vec2(4,0), vec2(5,0)
    //, vec2(0, 5)
);
vec2 target(int i) { return targets[i] + offset; }
#else
// procedural target dataset
const int H = 5;
const int W = 5;
vec2 targets[H * W];
vec2 target(int i) { return T*(vec2(i/W, i%W) - float(W/2)) + offset; }
