/* iadb */

vec2 tangent_mix(vec2 x, float alpha)
{
    // x = (1. - alpha) * x0 + alpha * x1
    float ps = 0.;
    vec2 t = vec2(0);
    for (int i=0 ; i<targets.length() ; ++i)
    {
        vec2 x1 = target(i);
        vec2 x0 = x1 - (x1 - x) / (1. - alpha);
        float p = exp(-dot2(x0) / 2.);
        t += p * (x1 - x0);
        ps += p;
    }
    return t / ps;
}

vec2 tangent_turn(vec2 x, float alpha)
{
    // x = cos(alpha) * x0 + sin(alpha) * x1
    alpha = pi / 2. * alpha;
    float c = cos(alpha);
    float s = sin(alpha);
    float ps = 0.;
    vec2 t = vec2(0);
    for (int i=0 ; i<targets.length() ; ++i)
    {
        vec2 x1 = target(i);
        vec2 x0 = (x - s * x1) / c;
        float p = exp(-dot2(x0) / 2.);
        t += p * (-s * x0 + c * x1);
        ps += p;
    }
    return pi / 2. * t / ps;
}

vec2 tangent(vec2 x, float alpha, int method)
{
    switch (method)
    {
    case 0: return tangent_mix(x, alpha);
    case 1: return tangent_turn(x, alpha);
    }
}

vec2 undo_last(vec2 x, float da, int method)
{
    switch (method)
    {
    case 0: return x * (1. - da);
    case 1: return x;
    }
}

vec2 predict_x1(vec4 x, float alpha, int method)
{
    switch (method)
    {
    case 0: return x.xy + (x.xy - x.zw) * (1. - alpha) * float(nstep);
    case 1: return x.xy; // not implemented
    }
}

/* setup */

vec3 resolution;
float f() { return resolution.y / (offset + 2. * radius + 2.).y; }
vec2 world(vec2 x)
{
    return resolution.xy / 2. + f() * (x - offset / 2.);
}

vec3 hue(float x)
{
    return pow(cos(4. * pi / 3. * vec3(x, x-.5, x-1.)) / 2. + .5, vec3(2));
}

vec2 mean_target()
{
    float ps = float(targets.length());
    vec2 x1 = vec2(0);
    for (int i=0 ; i<targets.length() ; ++i)
        x1 += target(i);
    return x1 / ps;
}

void blend(inout vec4 fragColor, vec4 color, float f)
{
    fragColor = mix(fragColor, vec4(color.rgb, 1), color.a * clamp(f, 0., 1.));
}

/* random numbers */

uvec4 pcg4d(uvec4 v)
{
    // https://www.shadertoy.com/view/XlGcRh
    // http://www.jcgt.org/published/0009/03/02/
    v = v * 1664525u + 1013904223u;

    v.x += v.y*v.w;
    v.y += v.z*v.x;
    v.z += v.x*v.y;
    v.w += v.y*v.z;

    v ^= v >> 16u;

    v.x += v.y*v.w;
    v.y += v.z*v.x;
    v.z += v.x*v.y;
    v.w += v.y*v.z;

    //v ^= v >> 16u;

    return v;
}

vec4 rand(uvec4 x)
{
    return vec4(pcg4d(x)) / float(~0u);
}

vec4 boxMuller(vec4 x)
{
    vec2 a = 2. * pi * x.yw;
    return sqrt(-2. * log(x.xz)).xxyy * vec4(cos(a), sin(a)).xzyw;
}

/* shapes */

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    // https://iquilezles.org/articles/distfunctions2d/
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

