float toroid (vec3 p, float r0, vec2 wh1, float r1) {
    return roundbox(vec2(length(p.xy)-r0,p.z),wh1,r1);
}
vec3 normal_toroid(vec3 p,float r0, vec2 wh1, float r1) {
    vec2 e = vec2(1e-5,0);
    return normalize(vec3(
        toroid(p+e.xyy,r0,wh1,r1)-toroid(p-e.xyy,r0,wh1,r1),
        toroid(p+e.yxy,r0,wh1,r1)-toroid(p-e.yxy,r0,wh1,r1),
        toroid(p+e.yyx,r0,wh1,r1)-toroid(p-e.yyx,r0,wh1,r1)
    ));

}
float angleDiff(float a1, float a2) {
    float diff = mod(a1 - a2, 2.0 * 3.14159265359);
    return (diff > 3.14159265359) ? (diff - 2.0 * 3.14159265359) : diff;
}
float toroid_coil (vec3 p, float r0, vec2 wh1, float r1, float ta, float tb, float n, vec2 wh2, float r2) 
{
    float b = wh2.x+r2;
float a0 = atan(p.y,p.x);
    float a1 = a0*n;
    if (length(p.xy) == 0.) return p.z - wh1.y;
    vec3 p0 = r0*vec3(cos(a0),sin(a0),0);
    vec3 q = p-p0;
    float a2 = atan(q.z,(dot(normalize(vec3(p.xy,0)),q)));
    a0 += angleDiff (a2,a1)/n;
    a1 = a0*n;
    p0 = r0*vec3(cos(a0),sin(a0),0);
    q = p-p0;
    vec2 v = length(q)*vec2(cos(a1),sin(a1));
    vec4 sq = squircle(v,wh1.x+b,wh1.y+b,r1+.5*b);
    vec3 p1 = p0 + sq.z*vec3(normalize(p.xy),0) + abs(sq.w)*sin(a1)*vec3(0,0,1) ;

    vec3 x = -sq.x*normalize(vec3(0,0,1))+sq.y*vec3(normalize(p.xy),0);
    vec3 z = sq.y*normalize(vec3(0,0,1))+sq.x*vec3(normalize(p.xy),0);
    vec3 y = cross(x,z);
    vec2 u = vec2(
        dot(p-p1,x),
        dot(p-p1,y)
    )*length(p1.xy)/r0;
    return roundbox(u,wh2,r2);

}

float map (vec3 p, out vec3 q) {

    float n = 3.+round(mod(iTime*123.,5.));

    float r0 = .6;
    vec2 wh1 = vec2(.2,.5);
    float r1 = .0;

    vec2 wh2 = vec2(.1,.2);
    float r2 = .01;

   float d = toroid_coil(p,r0,wh1,r1, 0.,360.,  n, wh2, r2);
    float d1 = toroid(p,r0,wh1-.022,.5*r1);
    q = (.5+max(sin(10.*iTime+vec3(3,2,1)),-.5));
    float d2 = min(d,d1);
    float d3 = abs(p.y+.7+.1*sin(.78*iTime)+.002*exp(-3.*d2)*sin(80.*(d2*exp(-.5*d2))))-.1;
    if (d3 < d1 && d3 < d) {
        q = vec3(.0);
        return (d3);
    }
    if (d < d1) {
        q *= 2.;
        return d;
    } 
    q = 1.-q;
    return d1;

}
float map (vec3 p) {
    vec3 q ;
    return map(p,q);
}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-3,0);
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
    ));
}
float smooth_map (vec3 p) {
    return smoothstep(-.01,+.01,map(p));
}
vec3 grad (vec3 p) {
    vec2 e = vec2(.005,0);
    float o = smooth_map(p-e.xyy);
    return vec3(
        smooth_map(p+e.xyy)-o,
        smooth_map(p+e.yxy)-o,
        smooth_map(p+e.yyx)-o
    )/e.x;
}
#define AA 1
Main {
    if ((iFrame)%160>0) discard;
    Q = vec4(0);
    for (int x = -AA; x <= AA; x++)
    for (int y = -AA; y <= AA; y++) {
    vec3 p = vec3(0,-.2,-1.5);
    vec2 u = 2.*(U+.5*vec2(x,y)/float(AA*2-1)-.5*R)/R.y;
    vec3 d = normalize(vec3(u,1));
    float t = .4+.5*sin(2.*iTime);
    p.xz *= mat2(cos(t),sin(t),-sin(t),cos(t));
    d.xz *= mat2(cos(t),sin(t),-sin(t),cos(t));

    vec3 
        p_refract = p, p_reflect = p,
        d_refract = d, d_reflect = d,
        q_reflect = vec3(0),
        q_refract = vec3(0);
    for (float i = min(iTime,0.0); i < 400.; i++) {
        if( p_refract.x>1000.0 ) break;
        vec3 q;
        {
            float m = min(abs(map(p_reflect,q)),1.);
            m = .5*m;
            if (m < .01) {
                m = .002;
                d_reflect = normalize(d_reflect+.001*grad(p_reflect));
                q_reflect += .1*q;
            }
            p_reflect += m*d_reflect;
        }
        {
            float m = min(abs(map(p_refract,q)),1.);
            m = .5*m;
            if (m < .01) {
                d_refract = normalize(d_refract-.001*grad(p_refract));
                q_refract += .01*q;
                m = .003;
            }
            p_refract += m*d_refract;
        }
    }
    Q += (.2*vec4(.6+.4*q_reflect,1)*texture(iChannel0,p_reflect)+
        0.3*vec4(.3+.7*q_refract,1)*texture(iChannel0,p_refract))/float((AA*2+1)*(AA*2+1));
