// CC0: Oversaturated web
//  Bit of tinkering thursday night
//  Not entirely happy with the colors but good enough for now
//  This a deriative of BigWing's: https://www.shadertoy.com/view/lscczl

#define TIME        iTime
#define RESOLUTION  iResolution

#define ROT(a)     mat2(cos(a), sin(a), -sin(a), cos(a))
#define OFF6(n)    (vec2(1.0, 0.0)*ROT(n*tau/6.))

#define BEZIER
const float
  pi        = acos(-1.)
, tau       = 2.*pi
, planeDist = .5
, furthest  = 6.
, fadeFrom  = 4.
, cutOff    = .975
;
const vec3  L  = 
  vec3(0.299, 0.587, 0.114)
;

const vec2 
  pathA = vec2(.31, .41)
, pathB = vec2(sqrt(2.0), 1.0)
;

const vec4 
  U = vec4(0, 1, 2, 3)
  ;

const vec2 off6[6] = vec2[6](
    OFF6(0.)
  , OFF6(1.)
  , OFF6(2.)
  , OFF6(3.)
  , OFF6(4.)
  , OFF6(5.)
  ); 

const vec2 noff6[6] = vec2[6](
    vec2(-1.0, 0.0)
  , vec2(-0.5, 0.5)
  , vec2( 0.5, 0.5)
  , vec2( 1.0, 0.0)
  , vec2( 0.5,-0.5)
  , vec2(-0.5,-0.5)
  ); 

vec3 offset(float z) {
  return vec3(pathB*sin(pathA*z), z);
}

vec3 doffset(float z) {
  return vec3(pathA*pathB*cos(pathA*z), 1.0);
}

vec3 ddoffset(float z) {
  return vec3(-pathA*pathA*pathB*sin(pathA*z), 0.0);
}

float tanh_approx(float x) {
  float x2 = x*x;
  return clamp(x*(27. + x2)/(27.+9.*x2), -1., 1.);
}


// License: Unknown, author: Martijn Steinrucken, found: https://www.youtube.com/watch?v=VmrIDyYiJBA
vec2 hextile(inout vec2 p) {
  // See Art of Code: Hexagonal Tiling Explained!
  // https://www.youtube.com/watch?v=VmrIDyYiJBA
  const vec2 sz       = vec2(1.0, sqrt(3.0));
  const vec2 hsz      = 0.5*sz;

  vec2 p1 = mod(p, sz)-hsz;
  vec2 p2 = mod(p - hsz, sz)-hsz;
  vec2 p3 = dot(p1, p1) < dot(p2, p2) ? p1 : p2;
  vec2 n = ((p3 - p + hsz)/sz);
  p = p3;

  n -= vec2(0.5);
  // Rounding to make hextile 0,0 well behaved
  return round(n*2.0)*0.5;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float hexagon(vec2 p, float r) {
  p = p.yx;
  const vec3 k = 0.5*vec3(-sqrt(3.0), 1.0, sqrt(4.0/3.0));
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}

// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 co) {
  co += 1.234;
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}

float dot2(vec2 p) {
  return dot(p, p);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float segment(vec2 p, vec2 a, vec2 b ) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float bezier(vec2 pos, vec2 A, vec2 B, vec2 C) {    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;
    float kk = 1.0/dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      
    float res = 0.0;
    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx-3.0*ky) + kz;
    float h = q*q + 4.0*p3;
    if( h >= 0.0) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h,-h)-q)/2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = clamp( uv.x+uv.y-kx, 0.0, 1.0 );
        res = dot2(d + (c + b*t)*t);
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3  t = clamp(vec3(m+m,-n-m,n-m)*z-kx,0.0,1.0);
        res = min( dot2(d+(c+b*t.x)*t.x),
                   dot2(d+(c+b*t.y)*t.y) );
        // the third root cannot be the closest
        // res = min(res,dot2(d+(c+b*t.z)*t.z));
    }
    return sqrt( res );
}

vec2 coff(float h) {
  float h0 = h;
  float h1 = fract(h0*9677.0);
  float t = 0.75*mix(0.5, 1.0, h0*h0)*(TIME+1234.5);
  return mix(0.1, 0.2, h1*h1)*sin(t*vec2(1.0, sqrt(0.5)));
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

vec3 alphaBlend(vec3 back, vec4 front) {
  // Based on: https://en.wikipedia.org/wiki/Alpha_compositing
  return mix(back, front.xyz, front.w);
}

vec4 alphaBlend(vec4 back, vec4 front) {
  float w = front.w + back.w*(1.-front.w);
  vec3 xyz = (front.xyz*front.w + back.xyz*back.w*(1.-front.w))/w;
  return w > 0. ? vec4(xyz, w) : vec4(0.);
}

vec4 plane(vec3 ro, vec3 rd, vec3 pp, vec3 off, float aa, float n) {
  vec2 p = (pp-off*U.yyx).xy;
  vec2 p2 = p;
  p2 *= ROT(tau*0.1*n+0.05*TIME);
  p2 += + 0.125*(ro.z-pp.z)*vec2(1.0)*ROT(tau*hash(vec2(n)));
  vec2 hp = p2;
  hp += 0.5;
  const float ga = 100.0;
  const float z = 1.0/3.0;
  hp /= z;
  vec2 hn = hextile(hp);

  float h0 = hash(hn+n);
  vec2 p0 = coff(h0);

  vec3 bcol = 0.5*(1.0+cos(vec3(0.0, 1.0, 2.0) + 2.0*(p2.x*p2.y+p2.x)-+0.33*n)); 
  vec3 col = vec3(0.0);

  for (int i = 0; i < 6; ++i) {
    float h1 = hash(hn+noff6[i]+n);
    vec2 p1 = off6[i]+coff(h1);

    float h2 = h0+h1;
    float fade = smoothstep(1.05, 0.85, distance(p0, p1)); 

    if (fade < 0.0125) continue;

#ifdef BEZIER
    vec2 p2 = 0.5*(p1+p0)+coff(h2);
    float dd = bezier(hp, p0, p2, p1);
#else
    float dd = segment(hp, p0, p1);
#endif
    float gd = abs(dd);
    gd *= sqrt(gd);
    gd = max(gd, 0.0005);

    col += fade*0.002*bcol/(gd);
  }

  {  
    float cd = length(hp-p0);
    float gd = abs(cd);
    gd *= (gd);
    gd = max(gd, 0.0005);
    col += 0.0025*sqrt(bcol)/(gd);
  }

  {
    float hd = hexagon(hp, 0.485);
    float gd = abs(hd);
    gd = max(gd, 0.005);
    col += 0.0005*bcol*bcol/(gd);
  }

  float l = dot(col, L);  

  return vec4(col, tanh_approx(sqrt(l)+dot(p, p)));
}

vec3 color(vec3 ww, vec3 uu, vec3 vv, vec3 ro, vec2 p) {
  float lp = length(p);
  vec2 np = p + 1./RESOLUTION.xy;
  float rdd = 2.0;

  vec3 rd = normalize(p.x*uu + p.y*vv + rdd*ww);
  vec3 nrd = normalize(np.x*uu + np.y*vv + rdd*ww);

  float nz = floor(ro.z / planeDist);

  vec4 acol = vec4(0);
  bool cutOut = false;

  vec3 skyCol = vec3(0.0);

  float maxpd = 0.;

  for (float i = 1.; i <= furthest; ++i) {
    float pz = planeDist*nz + planeDist*i;

    float pd = (pz - ro.z)/rd.z;

    if (pd > 0. && acol.w < cutOff) {
      vec3 pp = ro + rd*pd;
      maxpd = pd;
      vec3 npp = ro + nrd*pd;

      float aa = 3.*length(pp - npp);

      vec3 off = offset(pp.z);

      vec4 pcol = plane(ro, rd, pp, off, aa, nz+i);

      float nz = pp.z-ro.z;
      float fadeIn = smoothstep(planeDist*furthest, planeDist*fadeFrom, nz);
      float fadeOut = smoothstep(0., planeDist*.1, nz);
      pcol.w *= fadeOut*fadeIn;

      acol = alphaBlend(pcol, acol);
    } else {
      cutOut = true;
      acol.w = acol.w > cutOff ? 1. : acol.w;
      break;
    }

  }

  return alphaBlend(skyCol, acol);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
  vec2 r = RESOLUTION.xy, q = fragCoord/r.xy, pp = -1.0+2.0*q, p = pp;
  p.x *= r.x/r.y;

  float tdist = length(pp);
  float tm  = 0.2*planeDist*TIME+0.1*tdist;

  vec3 ro   = offset(tm);
  vec3 dro  = doffset(tm);
  vec3 ddro = ddoffset(tm);

  vec3 ww = normalize(dro);
  vec3 uu = normalize(cross(U.xyx+ddro, ww));
  vec3 vv = cross(ww, uu);
  vec3 col = color(ww, uu, vv, ro, p);
  col -= 0.02*U.zwx*(length(pp)+0.125);
  col *= smoothstep(1.5, 1.0, length(pp));
  col *= smoothstep(0.0, 10.0, TIME-2.0*(q.x-q.x*q.y));
  col = aces_approx(col);
  col = sqrt(col);
  fragColor = vec4(col, 1);
}

// SDFs: https://iquilezles.org/articles/distfunctions2d/

vec2 rotateUV(vec2 uv, float rotation)
{
    return vec2(cos(rotation) * (uv.x) + sin(rotation) * (uv.y),
                    cos(rotation) * (uv.y) - sin(rotation) * (uv.x));
}

float sdCircle(vec2 uv, float r)
{
    return (length(uv) - r);
}

float sdVesica(vec2 uv, float r, float d)
{
    uv = abs(uv);
    float b = sqrt(r*r-d*d);
    return (((uv.y-b)*d>uv.x*b) ? length(uv-vec2(0.0,b))
                             : length(uv-vec2(-d,0.0))-r);
}

float sdEquilateralTriangle(vec2 uv, float r)
{
    const float k = sqrt(3.0);
    uv.x = abs(uv.x) - r;
    uv.y = uv.y + r/k;
    if(uv.x+k*uv.y>0.0 ) uv = vec2(uv.x-k*uv.y,-k*uv.x-uv.y)/2.0;
    uv.x -= clamp(uv.x, -2.0*r, 0.0 );
    return -length(uv)*sign(uv.y);
}

float sdSegment(vec2 uv, vec2 a, vec2 b)
{
    vec2 pa = uv-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage(out vec4 Out, in vec2 UV)
{
    UV = (UV+UV-iResolution.xy)/iResolution.y;
    Out -= Out;

    // Face
    float cat =0.;
    float temp = sdCircle(UV, .46);                               // Face

    temp = min(temp,sdCircle(UV+vec2(.25,.14),.28));              // Left Cheek
    temp = min(temp,sdCircle(UV+vec2(.23,.1),.3));                // Left Cheek Top

    temp = min(temp,sdCircle(UV+vec2(-.25,.14),.28));             // Right Cheek
    temp = min(temp,sdCircle(UV+vec2(-.23,.1),.3));               // Right Cheek Top

    temp = min(temp,sdCircle(UV+vec2(0,.31),.2));                        // Chin
    temp = min(temp,sdCircle((UV+vec2(0,.225))*vec2(1,2),.5));           // Chin+

    temp = min(temp,sdVesica(rotateUV(UV+vec2(.27,-.19), 0.38), .7, .5));     // Left Ear
    temp = min(temp,sdVesica(UV+vec2(.39,-.085), .6,.5));                     // Left Ear Bottom    

    temp = min(temp,sdVesica(rotateUV(UV+vec2(-.27,-.19), -0.38), .7, .5));   // Right Ear
    temp = min(temp,sdVesica(UV+vec2(-.39,-.085), .6,.5));                    // Right Ear Bottom  

    temp = smoothstep(0.,.005,temp);
    cat = 1. - temp;

    //Eyes
    temp = sdVesica(rotateUV(UV+vec2(.21,.045), 0.95), 0.15, .07 +.1 * smoothstep(0.,1.,sin((iTime)*1.5)*3.-2.));
    temp = min(temp, sdVesica(rotateUV(UV+vec2(-.21,.045), -0.95), 0.15, .07 +.1 * smoothstep(0.,1.,sin(iTime*1.5)*3.-2.)));

    //Nose
    temp = min(temp, sdEquilateralTriangle(rotateUV(UV+vec2(0,.25),3.14), 0.02)-.03);
    temp = smoothstep(0.,.01, temp);
    cat = min(cat, temp);

    // Whiskers
    temp = sdSegment(UV, vec2(.2,-.25), vec2 (.6,-.16));
    temp = min(temp, sdSegment(UV, vec2(.22,-.275), vec2 (.58,-.26)));
    temp = min(temp, sdSegment(UV, vec2(.2,-.3), vec2 (.56,-.36)));
    temp = min(temp,sdSegment(UV, vec2(-.2,-.25), vec2 (-.6,-.16)));
    temp = min(temp, sdSegment(UV, vec2(-.22,-.275), vec2 (-.58,-.26)));
    temp = min(temp, sdSegment(UV, vec2(-.2,-.3), vec2 (-.56,-.36)));
    temp = smoothstep(0.,.007,temp);
    cat = abs(cat - temp);

    Out = mix(vec4(.212,.114,.196,1.), vec4(.945,0.910,0.902,1.), cat);
}

