#define AA  1   // (= 0/1) optional antialiasing

float PrCylDf (vec3 p, float r, float h);
float Minv3 (vec3 p);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
vec3 HsvToRgb (vec3 c);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float SmoothBump (float lo, float hi, float w, float x);
float Hashfv2 (vec2 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 sunDir, qHit;
vec2 gId;
float tCur, dstFar, gLim, szFac, rotAng, rotAngF, dTwist, vuAz;
int nFrame, idObj;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define VAR_ZERO min (nFrame, 0)
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d, a, r, h, sk;
  dMin = dstFar / szFac;
  p.xz -= HexToPix (gId);
  p.xz = Rot2D (p.xz, rotAngF);
  p.z -= 0.1;
  p /= szFac;
  q = p;
  h = 1.5;
  d = 0.8 * max (length (Rot2D (q.xz - vec2 (0., 0.07), 4. * pi * dTwist * q.y / h) -
     vec2 (0., 0.03)) - (0.06 - 0.015 * q.y / h + 0.005 * sin (4. * pi * q.y)), abs (q.y - h) - h);
  DMINQ (1);
  qq = p;
  qq.xz = Rot2D (qq.xz, rotAng);
  qq.y -= 3.1;
  qq.yz = Rot2D (qq.yz, 0.3 * pi + 0.1 * rotAng);
  q = qq;
  r = length (q.xz);
  d = max (r - 0.25, abs (q.y) - 0.1 * (1. - 1.5 * r));
  DMINQ (2);
  d = length (q - vec3 (0., clamp (q.y, -0.2, 0.), 0.)) - 0.15;
  DMINQ (3);
  sk = 1.;
  for (int k = 0; k < 2; k ++) {
    q = qq;
    q.xz = Rot2D (q.xz, sk * pi / 12.);
    a = atan (q.z, - q.x);
    q.xz = Rot2D (q.xz, 2. * pi * (floor (6. * a / (2. * pi) + 0.5) / 6.));
    q.x += 0.2;
    q.y -= 0.01 * sk + 0.4 * dot (q.xz, q.xz);
    sk = - sk;
    q.z *= 2.5;
    d = 0.5 * max (length (q.xz + vec2 (0.4, 0.)) - 0.5, abs (q.y) - 0.03 * (1.2 - length (q.xz)));
    DMINQ (4);
  }
  q = qq;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (12. * ((r > 0.) ? atan (q.z, - q.x) /
     (2. * pi) : 0.) + 0.5) / 12.));
  q.y -= 0.15;
  q.x = abs (q.x + 0.04 + 0.4 * q.y * (1. + 2. * q.y)) - 0.02;
  d = 0.8 * PrCylDf (q.xzy, 0.003, 0.17);
  DMINQ (5);
  return szFac * dMin;
}

void SetFlwConf ()
{
  float s;
  s = length (vec3 (gId.xy, gId.x + gId.y)) / gLim;
  szFac = 0.65 * (1. - 0.2 * s * s + 0.2 * (Hashfv2 (17. * gId + 99.) - 0.5));
  s = Hashfv2 (23. * gId + 99.) - 0.5;
  dTwist = sign (s);
  s = max (0.1, abs (s)) * dTwist;
  rotAngF = 0.2 * pi * s;
  rotAng = vuAz + 0.3 * pi * (sin (pi * s * (tCur + 10.)) + s) - rotAngF;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv;
  vec2 edN[3], pM;
  float dHit, d, s, eps;
  bool cNu;
  if (rd.x == 0.) rd.x = 1e-4;
  if (rd.z == 0.) rd.z = 1e-4;
  eps = 1e-3;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = 1. / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]), dot (ro.xz, edN[2]));
  dHit = 0.;
  gId = PixToHex (ro.xz);
  pM = HexToPix (gId);
  cNu = true;
  for (int j = VAR_ZERO; j < 220; j ++) {
    if (cNu) {
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
      SetFlwConf ();
      cNu = false;
    }
    if (length (vec3 (gId.xy, gId.x + gId.y)) <= gLim) d = ObjDf (ro + dHit * rd);
    else d = dstFar;
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      gId = PixToHex (ro.xz + dHit * rd.xz);
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
      cNu = true;
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  gIdP = vec2 (-999.);
  d = 0.01;
  for (int j = VAR_ZERO; j < 20; j ++) {
    p = ro + d * rd;
    gId = PixToHex (p.xz);
    if (length (vec3 (gId.xy, gId.x + gId.y)) <= gLim) {
      if (gId != gIdP) {
        gIdP = gId;
        SetFlwConf ();
      }
      h = ObjDf (p);
      sh = min (sh, smoothstep (0., 0.05 * d, h));
      d += h;
    } else d += 0.2;
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd, a;
  a = atan (rd.z, - rd.x) / (2. * pi) + 0.5;
  if (rd.y > -0.02 && rd.y < 0.04 * mix (Fbm1 (128. * (a + 1.)), Fbm1 (128. * a), a)) {
    col = vec3 (0.3, 0.41, 0.55);
  } else {
    q = 0.01 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.4, 0.5, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) *
       min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return 0.8 * col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  vec2 vf;
  float dstObj, r, a, sh, hue, glit;
  bool isBg;
  gLim = 20.;
  isBg = true;
  glit = 0.;
  vf = vec2 (0.);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    isBg = false;
    ro += dstObj * rd;
    gId = PixToHex (ro.xz);
    vn = ObjNf (ro);
    hue = Hashfv2 (gId * vec2 (17., 27.) + 0.5);
    if (idObj >= 2) {
      r = length (qHit.xz);
      a = (r > 0.) ? atan (qHit.z, - qHit.x) / (2. * pi) : 0.;
    }
    if (idObj == 1) {
      col4 = vec4 (0.7, 0.4, 0., 0.1);
      vf = vec2 (64., 2.);
    } else if (idObj == 2) {
      col4 = vec4 (HsvToRgb (vec3 (fract (hue + 0.6), 1., 0.7)) *
         (0.7 + 0.3 * SmoothBump (0.2, 0.8, 0.005, fract (32. * r + a))), 0.1);
      vf = vec2 (32., 1.);
    } else if (idObj == 3) {
      col4 = vec4 (HsvToRgb (vec3 (fract (hue + 0.1), 0.7, 1.)) *
         (0.7 + 0.3 * SmoothBump (0.05, 0.95, 0.01, fract (8. * a))), 0.1);
      vf = vec2 (64., 1.);
    } else if (idObj == 4) {
      col4 = vec4 (HsvToRgb (vec3 (fract (hue), 0.7 + 0.3 * fract (7. * hue), 1.)), 0.05);
      col4.rgb *= 1. - 0.1 * SmoothBump (0.05, 0.95, 0.01, fract (16. * a));
      glit = 512. * step (0.01, max (0., dot (vn, sunDir))) *
         pow (max (0., dot (sunDir, reflect (rd, vn))), 16.) *
         pow (1. - 0.6 * abs (dot (normalize (sunDir - rd), VaryNf (1024. * ro, vn, 2.))), 8.);
      vf = vec2 (128., 0.1);
    } else if (idObj == 5) {
      col4 = vec4 (mix (vec3 (0.9, 0.9, 0.2), vec3 (1., 0.1, 0.1), step (0.16, qHit.y)),
         0.1 - step (0.16, qHit.y));
    }
  } else if (rd.y < 0.) {
    isBg = false;
    dstObj = - ro.y / rd.y;
    ro += dstObj * rd;
    col4 = 0.9 * mix (vec4 (0.4, 0.5, 0.3, 0.), vec4 (0., 0.5, 0.1, 0.),
       smoothstep (0.2, 0.8, Fbm2 (0.5 * ro.xz)));
    gId = PixToHex (ro.xz);
    if (length (vec3 (gId.xy, gId.x + gId.y)) <= gLim) col4 *= 0.7 +
       0.3 * smoothstep (0.1, 0.2, length (ro.xz - vec2 (0., 0.1) - HexToPix (gId)));
    qHit = ro;
    vn = vec3 (0., 1., 0.);
    vf = vec2 (1., (1. - smoothstep (0.4, 0.7, dstObj / dstFar)));
  }
  if (! isBg) {
    if (vf.y > 0.) vn = VaryNf (vf.x * qHit, vn, vf.y);
    sh = (dstObj < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
    col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
       step (0.95, sh) * (glit + col4.a * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.));
    if (dstObj >= dstFar) col = mix (col, 0.8 * vec3 (0.3, 0.41, 0.55), pow (1. + rd.y, 16.));
  } else {
    col = SkyBgCol (ro, rd);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, sr, zmFac;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.05 * pi;
  zmFac = 4.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    zmFac += 3. * abs (az);
    el += pi * mPtr.y;
  } else {
    az += 0.7 * pi * sin (0.005 * pi * tCur);
    el += 0.02 * pi * sin (0.021 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, -0.02 * pi);
  vuAz = az;
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -40.);
  sunDir = vuMat * normalize (vec3 (0.7, 1.5, -1.));
  sunDir.xz = Rot2D (sunDir.xz, 0.2 * pi * cos (0.03 * pi * tCur));
  dstFar = 80.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1. / sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (mod (dot (p, vec2 (37., 39.)), 2. * pi)) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (mod (p + vec2 (0., 1.), 2. * pi)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (mod (dot (p, cHashVA2) + vec2 (0., cHashVA2.x), 2. * pi)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

const float PI=3.14159265359;

const float TWO_PI=6.28318530718;

mat2 rotation2d(float angle){
    float s=sin(angle);
    float c=cos(angle);

    return mat2(
        c,-s,
        s,c
    );
}

mat4 rotation3d(vec3 axis,float angle){
    axis=normalize(axis);
    float s=sin(angle);
    float c=cos(angle);
    float oc=1.-c;

    return mat4(
        oc*axis.x*axis.x+c,oc*axis.x*axis.y-axis.z*s,oc*axis.z*axis.x+axis.y*s,0.,
        oc*axis.x*axis.y+axis.z*s,oc*axis.y*axis.y+c,oc*axis.y*axis.z-axis.x*s,0.,
        oc*axis.z*axis.x-axis.y*s,oc*axis.y*axis.z+axis.x*s,oc*axis.z*axis.z+c,0.,
        0.,0.,0.,1.
    );
}

vec2 rotate(vec2 v,float angle){
    return rotation2d(angle)*v;
}

vec3 rotate(vec3 v,vec3 axis,float angle){
    return(rotation3d(axis,angle)*vec4(v,1.)).xyz;
}

