vec3 mod289(vec3 x)
{
    return x-floor(x*(1./289.))*289.;
}

vec4 mod289(vec4 x)
{
    return x-floor(x*(1./289.))*289.;
}

vec4 permute(vec4 x)
{
    return mod289(((x*34.)+1.)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
    return 1.79284291400159-.85373472095314*r;
}

vec3 fade(vec3 t){
    return t*t*t*(t*(t*6.-15.)+10.);
}

// Classic Perlin noise
float cnoise(vec3 P)
{
    vec3 Pi0=floor(P);// Integer part for indexing
    vec3 Pi1=Pi0+vec3(1.);// Integer part + 1
    Pi0=mod289(Pi0);
    Pi1=mod289(Pi1);
    vec3 Pf0=fract(P);// Fractional part for interpolation
    vec3 Pf1=Pf0-vec3(1.);// Fractional part - 1.0
    vec4 ix=vec4(Pi0.x,Pi1.x,Pi0.x,Pi1.x);
    vec4 iy=vec4(Pi0.yy,Pi1.yy);
    vec4 iz0=Pi0.zzzz;
    vec4 iz1=Pi1.zzzz;

    vec4 ixy=permute(permute(ix)+iy);
    vec4 ixy0=permute(ixy+iz0);
    vec4 ixy1=permute(ixy+iz1);

    vec4 gx0=ixy0*(1./7.);
    vec4 gy0=fract(floor(gx0)*(1./7.))-.5;
    gx0=fract(gx0);
    vec4 gz0=vec4(.5)-abs(gx0)-abs(gy0);
    vec4 sz0=step(gz0,vec4(0.));
    gx0-=sz0*(step(0.,gx0)-.5);
    gy0-=sz0*(step(0.,gy0)-.5);

    vec4 gx1=ixy1*(1./7.);
    vec4 gy1=fract(floor(gx1)*(1./7.))-.5;
    gx1=fract(gx1);
    vec4 gz1=vec4(.5)-abs(gx1)-abs(gy1);
    vec4 sz1=step(gz1,vec4(0.));
    gx1-=sz1*(step(0.,gx1)-.5);
    gy1-=sz1*(step(0.,gy1)-.5);

    vec3 g000=vec3(gx0.x,gy0.x,gz0.x);
    vec3 g100=vec3(gx0.y,gy0.y,gz0.y);
    vec3 g010=vec3(gx0.z,gy0.z,gz0.z);
    vec3 g110=vec3(gx0.w,gy0.w,gz0.w);
    vec3 g001=vec3(gx1.x,gy1.x,gz1.x);
    vec3 g101=vec3(gx1.y,gy1.y,gz1.y);
    vec3 g011=vec3(gx1.z,gy1.z,gz1.z);
    vec3 g111=vec3(gx1.w,gy1.w,gz1.w);

    vec4 norm0=taylorInvSqrt(vec4(dot(g000,g000),dot(g010,g010),dot(g100,g100),dot(g110,g110)));
    g000*=norm0.x;
    g010*=norm0.y;
    g100*=norm0.z;
    g110*=norm0.w;
    vec4 norm1=taylorInvSqrt(vec4(dot(g001,g001),dot(g011,g011),dot(g101,g101),dot(g111,g111)));
    g001*=norm1.x;
    g011*=norm1.y;
    g101*=norm1.z;
    g111*=norm1.w;

    float n000=dot(g000,Pf0);
    float n100=dot(g100,vec3(Pf1.x,Pf0.yz));
    float n010=dot(g010,vec3(Pf0.x,Pf1.y,Pf0.z));
    float n110=dot(g110,vec3(Pf1.xy,Pf0.z));
    float n001=dot(g001,vec3(Pf0.xy,Pf1.z));
    float n101=dot(g101,vec3(Pf1.x,Pf0.y,Pf1.z));
    float n011=dot(g011,vec3(Pf0.x,Pf1.yz));
    float n111=dot(g111,Pf1);

    vec3 fade_xyz=fade(Pf0);
    vec4 n_z=mix(vec4(n000,n100,n010,n110),vec4(n001,n101,n011,n111),fade_xyz.z);
    vec2 n_yz=mix(n_z.xy,n_z.zw,fade_xyz.y);
    float n_xyz=mix(n_yz.x,n_yz.y,fade_xyz.x);
    return 2.2*n_xyz;
}

float pow5(const in float v){
    float v2=v*v;
    return v2*v2*v;
}

vec3 schlick(const in vec3 f0,const in float f90,const in float VoH){
    float f=pow5(1.-VoH);
    return f+f0*(f90-f);
}

vec3 fresnel(vec3 f0,vec3 normal,vec3 view){
    return schlick(f0,1.,dot(view,normal));
}

float sdSphere(vec3 p,float r)
{
    return length(p)-r;
}

float opUnion(float d1,float d2)
{
    return min(d1,d2);
}

vec2 opUnion(vec2 d1,vec2 d2)
{
    return(d1.x<d2.x)?d1:d2;
}

vec3 distort(vec3 p){
    float frequency=1.7;
    float offset=cnoise(p/frequency+iTime*.5);
    float t=(p.y+offset)*PI*12.;
    float noise=(sin(t)*p.x+cos(t)*p.z)*2.;
    p+=noise*.01;
    return p;
}

vec2 map(vec3 p){
    p-=vec3(0.,0.,-2.);

    // orbit
    vec2 mouse=iMouse.xy/iResolution.xy;
    p.yz=rotate(p.yz,-mouse.y*PI+1.);
    p.xz=rotate(p.xz,-mouse.x*TWO_PI);

    p=distort(p);

    vec2 d=vec2(1e10,0.);
    float d1=sdSphere(p,1.5);
    d=opUnion(d,vec2(d1,2.));
    return d*.5;
}

vec3 calcNormal(in vec3 p)
{
    const float h=.0001;
    const vec2 k=vec2(1,-1);
    return normalize(k.xyy*map(p+k.xyy*h).x+
    k.yyx*map(p+k.yyx*h).x+
    k.yxy*map(p+k.yxy*h).x+
    k.xxx*map(p+k.xxx*h).x);
}

vec3 render(vec2 uv){
    vec3 col=vec3(0.);

    uv=(uv-.5)*2.;
    uv.x*=iResolution.x/iResolution.y;

    vec3 ro=vec3(0.,0.,1.);
    vec3 rd=normalize(vec3(uv,0.)-ro);

    float depth=0.;
    for(int i=0;i<64;i++){
        vec3 p=ro+rd*depth;
        vec2 t=map(p);
        float d=t.x;
        float m=t.y;
        depth+=d;

        if(d<.01){
            // col=vec3(1.);
            vec3 normal=calcNormal(p);
            // col=normal;

            // fresnel
            vec3 viewDir=normalize(ro-p);
            col=fresnel(vec3(0.),normal,viewDir);

            break;
        }
    }

    return col;
}

vec3 getSceneColor(vec2 fragCoord){
    vec2 uv=fragCoord/iResolution.xy;
    vec3 col=render(uv);
    return col;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec3 tot=vec3(0.);

    float AA_size=2.;
    float count=0.;
    for(float aaY=0.;aaY<AA_size;aaY++)
    {
        for(float aaX=0.;aaX<AA_size;aaX++)
        {
            tot+=getSceneColor(fragCoord+vec2(aaX,aaY)/AA_size);
            count+=1.;
        }
    }
    tot/=count;

    fragColor=vec4(tot,1.);
}

// https://www.desmos.com/calculator/n4mfhffj1n
float f(float x, float v)
{
    if (abs(v) < .0001) v = .0001;
    float p = pow(2., v);
    return (1. - pow(p, -x)) / (1. - 1. / p);
}

vec3 colormap(float x)
{
    //float t = .1 * iTime;
    //float t = .75 - 1.1 * x;
    //float t = .25 * x;
    float t = .6 + .8 * x;

    // https://www.desmos.com/calculator/sdqk904uu9
    vec3 tone = 10. * vec3(
        cos(6.283 * t),
        cos(6.283 * (t - .3333)),
        cos(6.283 * (t - .6667))
    );

    x = smoothstep(-.04, 1., x);
    vec3 c = vec3(
        f(x, tone.r),
        f(x, tone.g),
        f(x, tone.b)
    );

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = colormap(uv.x);
    col = pow(col, vec3(.45));
    fragColor = vec4(col,1.0);
}

// ---------------------------------------------------------------------------------------
//	Created by fenix in 2023
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  Inspired by Kenny Erleben's 2007 paper "Velocity-Based Shock Propagation for Multibody
//  Dynamics Animation"
//
//      https://dl.acm.org/doi/10.1145/1243980.1243986
//
//  This is definitely only inspired by the original shock propagation idea. As described
//  in the paper, the way I understand it, it would fit into an traditional iterative
//  solver, freezing objects sequentially as you get farther from a boundary. But to use
//  this idea in a shader, I have to manage with a single iteration.
//
//  So, the way it works is that each particle keeps track of a "shock direction" that
//  tells it the direction to the nearest wall. Green is to the right, blue to the left,
//  and red is supported from below. The shock direction is seeded from the walls and
//  diffuses through the particles. Particles resist collision impules and position
//  correction against their shock direction. That's where the stability comes from:
//  particles "below" other particles are heavier. When particles get compressed in
//  an area, they are able to flow along the shock directions to a clear space.
//
//  I claim this is at least conceptually similar to the original shock propagation idea.
//  In both cases, we're giving solver priority to objects closer to the ground. Maybe
//  there's a better name for this method, but until I find one I'll keep comparing it to
//  Erleben's shock propagation. :) I've used a more primitive method for stability in
//  some shaders already, like
//
//      Candy Avalanche                https://shadertoy.com/view/dlfSz4
//
//  In that shader, solver priority is determined entirely by particle height. This works
//  for that application, but it creates artifacts when you try to use it for a bunch of
//  particles resting in a container with walls on all sides; the particles can end up
//  pushing each other up the walls. With the shock directions, the particles near the
//  side walls can resist motions towards the walls instead of only downwards. The
//  smoothness of the shock direction field can then prevent artifacts.
//
//  An additional hack is needed to handle the moving boundary. The "local wall velocity"
//  is also seeded at wall collisions and diffused through the particles. This prevents
//  us from relying on collisions only to move around the particles in the bucket and in
//  front of the bulldozer wall. Hold down the alt key to see the wall velocities.
//
//      *shift to render velocities*
//      *alt to render local wall velocity field*
//      *space to reset*
//
//  Buffer A, B, and C perform particle simulation. Only A updates the particle neighbors.
//  Buffer D computes screen-space voronoi for render.
//
// ---------------------------------------------------------------------------------------

void renderParticle(fxParticle v, vec2 p, uint id, inout vec4 O)
{
    float e = 2. / R.y;
    float d = fxLinePointDist(v.pos, v.pos - v.vel, p);

    vec3 color = v.sd == vec2(0) ? vec3(1, 0, 0) : sin(atan(abs(v.sd.y * .1), v.sd.x) + vec3(0, 11, 33));
    color *= (id % 2U == 0U) ? .7 : 1.;
    color = mix(color, vec3(1), .3);
    if (keyDown(KEY_ALT))
        color = 5000. * length(v.wv) * sin(atan(v.wv.y, abs(v.wv.x)) + vec3(0, 11, 33));
    if (keyDown(KEY_SHIFT))
        color = pow(abs(v.vel.yxx) * 1e3, vec3(.3));
    O.xyz = mix(O.rgb, color, smoothstep(PARTICLE_SIZE + e, PARTICLE_SIZE - e, d));
}

void mainImage(out vec4 O, vec2 u)
{
    computeMaxParticles();
    vec2 p = (u - .5 * R.xy) / R.y;
    uvec4 old = fxGetClosest(ivec2(u));
    fxState s = fxGetState();
    O = vec4(.2);

    // render particles
    for (int i = 0; i < 4; ++i)
    {
        uint id = old[i];
        if (id == -1U || id >= g_MaxParticles) break;
        fxParticle v = fxGetParticle(id);
        renderParticle(v, p, id, O);
    }

    float d = sdScene(p, R, s.t);

    O = mix(O, vec4(1), smoothstep(0., -1. / R.y, d));
}

