const uint IDEAL_MAX_PARTICLES = 15000U;
const float PARTICLE_SIZE = .003;
const float PI = 3.141592653589793;
const float DT = .0002; // bucket animation increment (t cycles from 0 to 1)

// If not applied carefully, the shock direction can create the a situation where
// the particles get shoved towards the center of a container. This hack biases
// the shock direction towards vertical, ensuring we get smaller green and blue
// areas and more red, and preventing particles from being shoved towards the center.
const vec2 SHOCK_BIAS = vec2(1, 1.1);

#define R iResolution.xy

// PERSISTENT STATE

struct fxState
{
    float res;  // tracks resolution changes
    float t;    // animation time
};

#define fxGetState() fxGetStateImpl(iChannel1)
fxState fxGetStateImpl(sampler2D sampler)
{
    vec4 data = texelFetch(sampler, ivec2(0), 0);

    fxState s;
    s.res = data.x;
    s.t = data.y;

    return s;
}

vec4 fxPutState(fxState s)
{
    return vec4(s.res, s.t, 0, 0);
}

bool fxIsStatePixel(vec2 u)
{
    return ivec2(u) == ivec2(0);
}

//returns the ids of the four closest particles from the input
#define fxGetClosest(X) fxGetClosestImpl(iChannel1, X)
uvec4 fxGetClosestImpl(sampler2D sampler, ivec2 xy)
{
    return floatBitsToUint(texelFetch(sampler, xy, 0));
}

// PARTICLE STRUCTURE

#define UL_NEIGHBORS 0U
#define UR_NEIGHBORS 1U
#define LL_NEIGHBORS 2U
#define LR_NEIGHBORS 3U
#define POS 4U
#define SDAV 5U
#define NUM_PARTICLE_DATA_TYPES 6U

uint g_MaxParticles = 0U;

// must be called in each buffer before g_MaxParticles is correct
#define computeMaxParticles() computeMaxParticlesImpl(R)
void computeMaxParticlesImpl(vec2 res)
{
    g_MaxParticles = min(IDEAL_MAX_PARTICLES, uint(res.x * res.y) / NUM_PARTICLE_DATA_TYPES);
}

//returns the location of the particle within the particle buffer corresponding with the input id 
#define fxLocFromID(X, Y) fxLocFromIDImpl(int(R.x), X, Y)
ivec2 fxLocFromIDImpl(uint width, uint id, uint dataType)
{
    uint index = id * NUM_PARTICLE_DATA_TYPES + dataType;
    return ivec2( index % width, index / width);
}

struct fxParticle
{
    vec2 pos;     // position
    vec2 vel;     // velocity
    vec2 sd;      // shock direction
    vec2 wv;      // local wall velocity

    uvec4 nbs[4]; // neighbors
};

//get the particle corresponding to the input id
#define fxGetParticle(X) fxGetParticleImpl(iChannel0, uint(R.x), X)
fxParticle fxGetParticleImpl(sampler2D sampler, uint resolutionWidth, uint id)
{
    vec4 particleData0 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, UL_NEIGHBORS), 0);
    vec4 particleData1 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, UR_NEIGHBORS), 0);
    vec4 particleData2 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, LL_NEIGHBORS), 0);
    vec4 particleData3 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, LR_NEIGHBORS), 0);
    vec4 particleData4 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, POS), 0);
    vec4 particleData5 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, SDAV), 0);

    fxParticle particle;
    particle.nbs[0] = floatBitsToUint(particleData0);
    particle.nbs[1] = floatBitsToUint(particleData1);
    particle.nbs[2] = floatBitsToUint(particleData2);
    particle.nbs[3] = floatBitsToUint(particleData3);
    particle.pos = particleData4.xy;
    particle.vel = particleData4.zw;
    particle.sd = particleData5.xy;
    particle.wv = particleData5.zw;

    return particle;
}

vec4 fxSaveParticle(fxParticle p, uint dataType)
{    
    switch(dataType)
    {
    case UL_NEIGHBORS:
        return uintBitsToFloat(p.nbs[0]);
    case UR_NEIGHBORS:
        return uintBitsToFloat(p.nbs[1]);
    case LL_NEIGHBORS:
        return uintBitsToFloat(p.nbs[2]);
    case LR_NEIGHBORS:
        return uintBitsToFloat(p.nbs[3]);
    case POS:  
        return vec4(p.pos, p.vel);
    case SDAV:  
        return vec4(p.sd, p.wv);
    }
}

#define fxGetParticleData(X, Y) fxGetParticleDataImpl(iChannel0, uint(R.x), X, Y)
vec4 fxGetParticleDataImpl(sampler2D sampler, uint resolutionWidth, uint id, uint dataType)
{
    return texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, dataType), 0);
}

// UTILITIES

#define keyClick(ascii)   ( texelFetch(iChannel3,ivec2(ascii,1),0).x > 0.)
#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

#define KEY_SHIFT 16
#define KEY_ALT 18
#define KEY_SPACE 32

uvec4 hash(uvec4 x){
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    //x = (x >> 16u) ^ x;
    return x;
}

//hashing noise by IQ
float hash( int k ) {
    uint n = uint(k);
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.0;
}

// Integer Hash - II by iq
// https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;  // GLIB C

vec3 hash3( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

float length2(vec2 v) { return dot(v, v); }
float sqr(float x) { return x * x; }

float fxLinePointDist(vec2 a, vec2 b, vec2 p)
{
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);// proj coord on line
    return sqrt(length2(p - b * h)); // squared dist to segment
}

// https://iquilezles.org/articles/distfunctions2d/
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// https://iquilezles.org/articles/distfunctions2d/
float sdArc( in vec2 p, in vec2 sc, in float ra, float rb )
{
    // sc is the sin/cos of the arc's aperture
    p.x = abs(p.x);
    return ((sc.y*p.x>sc.x*p.y) ? length(p-sc*ra) : 
                                  abs(length(p)-ra)) - rb;
}

mat2 rot2(float a)
{
    vec2 sc = vec2(sin(a), cos(a));
    return mat2(sc.y, sc.x, -sc.x, sc.y);
}

// insert new particle at distance d_ with index i_ into i, d arrays, sorting by d
void insertionSort(inout uvec4 i, inout vec4 d, uint i_, float d_)
{	
    if(any(equal(uvec4(i_),i))) return;
    if     (d_ < d[0])             
        i = uvec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = uvec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = uvec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = uvec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

// SCENE FUNCTIONS

// the animation of the buckets
vec2 path(float t)
{
    // piecewise, linear for beginning and end
    if (t < .25) return vec2(.5, -3. * t + .65);
    if (t > .5) return vec2(1.6 - 3. * t, -.4);

    // bezier to turn the corner smoothly
    t *= 4.;
    t -= 1.;
    vec2 a = vec2(.5, t * -.2 - .1);
    vec2 b = vec2(.5 - .4 * t, -.4);
    return mix(a, b, t);
}

// signed distance field of one bucket
float sdBucket(vec2 p, float t)
{
    vec2 cp = path(t); // bucket location

    float r = max(0., t - .35); // rotation angle
    r = -4. * sqrt(r);

    const float theta = 2.0;
    const vec2 sc = vec2(sin(theta), cos(theta));
    return sdArc((p * vec2(1, -1) - cp) * rot2(r), sc, 0.2, 0.01);
}

const float BULLDOZER_HEIGHT = .25;

// scene (walls) signed distance field, for rendering and collision
float sdScene(vec2 p, vec2 res, float t)
{
    // walls and floor
    float d = p.x + .5 * res.x / res.y;
    d = min(d, .5 * res.x / res.y - p.x);
    d = min(d, p.y + .5);

    // square leaky bucket
    d = min(d, sdBox(p - vec2(-.75, -.05), vec2(.01,  .1 )));
    d = min(d, sdBox(p - vec2(-.45, -.05), vec2(.01,  .1 )));
    d = min(d, sdBox(p - vec2(-.685, -.15), vec2(.075, .01)));
    d = min(d, sdBox(p - vec2(-.515, -.15), vec2(.075, .01)));

    // slanted walls
    d = min(d, sdBox((p - vec2(-.28,  .03)) * rot2(-.8), vec2(.2, .01)));
    d = min(d, sdBox((p - vec2( .05, -.05)) * rot2(.8), vec2(.2, .01)));

    // moving buckets
    float t2 = fract(t + .5);
    d = min(d, min(sdBucket(p, t), sdBucket(p, t2)));

    // bulldozer walls
    float t3 = fract(t + .9);
    float t4 = fract(t + .4);
    d = min(d, sdBox(p - vec2(-1. + t3 * 1.8, min(-.5, 1.05 - 2. * t3)), vec2(.01, BULLDOZER_HEIGHT)));
    d = min(d, sdBox(p - vec2(-1. + t4 * 1.8, min(-.5, 1.05 - 2. * t4)), vec2(.01, BULLDOZER_HEIGHT)));

    return d;
}

// the outward normal of the scene at a point
vec2 normScene( vec2 p, vec2 res, float t )
{
	const vec2 d = vec2(.001, 0);

   	float x1 = sdScene(p + d.xy, res, t); 
    float x0 = sdScene(p - d.xy, res, t); 
    float dx = x1 - x0;

    float y1 = sdScene(p + d.yx, res, t); 
    float y0 = sdScene(p - d.yx, res, t); 
    float dy = y1 - y0;

	return normalize(vec2(dx, dy));
}

// the velocity of the scene at a point
vec2 velScene(vec2 p, float t)
{
    // bucket 1
    if (sdBucket(p, t) < .01)
        return (path(t) - path(t + DT / 3.)) * vec2(-1, 1);

    // bucket 2
    float t2 = fract(t + .5);
    if(sdBucket(p, t2) < .01)
        return (path(t2) - path(t2 + DT / 3.)) * vec2(-1, 1);

    // bulldozer walls
    float t3 = fract(t + .9);
    float t4 = fract(t + .4);
    if (sdBox(p - vec2(-0.995 + t3 * 1.8, -.5), vec2(.02, BULLDOZER_HEIGHT)) < 0. ||
        sdBox(p - vec2(-0.995 + t4 * 1.8, -.5), vec2(.02, BULLDOZER_HEIGHT)) < 0.)
        return vec2(7e-5, 0);

    return vec2(0);
}

// PARTICLE SIM

const vec2 GRAVITY = vec2(0., -.00001);
const float COLLISION_STIFFNESS = .3;
const float BOUNDARY_ELASTICITY = 1.5;
const float BOUNDARY_DIST = PARTICLE_SIZE;
const float MAX_SPEED = PARTICLE_SIZE * .5;

#define updateParticle(A, B, C, D, E) updateParticleImpl(A, B, C, D, E, iFrame, R, iChannel0, iMouse)
vec4 updateParticleImpl(fxState s, fxParticle p, uint id, uint dataType, float toff, int iFrame, vec2 res, sampler2D par, vec4 iMouse)
{
    if (iFrame == 0 || s.res < 0.)
    {
        // init particles
        vec3 h1 = hash3((id + uvec3(iFrame)) * uvec3(3, 6, 9));
        //h1.x = pow(h1.x, 1.3);
        h1 -= .5;
        h1.y = h1.y * .3 - .2;
        h1.x *= res.x / res.y;

        p.pos = h1.xy;
        p.vel = vec2(0);
        p.sd = vec2(0, 1);
        p.wv = vec2(0);
    }
    else
    {
        p.vel = p.vel + GRAVITY; // integrate velocity

        if (length(p.vel) > MAX_SPEED)
            p.vel = normalize(p.vel) * MAX_SPEED; // clamp velocity

        p.pos += p.vel; // integrate position
        p.sd *= .5; // dissipate shock direction

        vec2 imp = vec2(0); // collision impulse
        vec2 nsd = p.sd; // new support direction
        vec2 wv = p.wv; // wall velocity
        float nt = 1.01; // wall velocity normalization factor (above one to create damping)
        bool col = false; // was there a collision
        for(int i = 0; i < 4; i++)
        {
            uvec4 nbs = p.nbs[i];
            for (int j = 0; j < 4; ++j)
            {
                uint cid = nbs[j];
                if(cid==id || cid == -1U || cid >= g_MaxParticles) continue;

                fxParticle n = fxGetParticleImpl(par, uint(res.x), cid);

                vec2 dp = p.pos - n.pos;
                float d2 = length2(dp) + PARTICLE_SIZE * 1e-4;

                if (d2 < sqr(PARTICLE_SIZE * 5.))
                {
                    vec2 dir = dp / sqrt(d2);

                    nsd += n.sd; // diffuse shock direction

                    float wv2 = length2(n.wv);
                    if (wv2 > 1e-12)
                    {
                        float x = max(0., dot(n.wv / sqrt(wv2), dir) + .3);
                        nt += x;    // normalization
                        wv += n.wv * x;  // diffuse wall velocity
                    }

                    if (d2 < sqr(PARTICLE_SIZE * 2.))
                    {
                        col = true;

                        // position correction (ignore if shock direction disagrees)
                        if (dot(p.sd, dir) > -.2)
                            p.pos = mix(p.pos, n.pos + 2. * PARTICLE_SIZE * dir, COLLISION_STIFFNESS);

                        // collision impulse
                        vec2 rv = n.vel - p.vel;
                        imp += max(0., dot(rv, dir)) * dir;
                    }
                }
            }
        }

        float sd2 = length2(nsd);
        if (sd2 > 1e-9)
            p.sd = mix(p.sd, nsd * SHOCK_BIAS / sqrt(sd2), .9); // record new shock direction

        p.wv = wv / nt; // record new local wall velocity

        if (dot(p.sd, imp) < -.2) // resist impulses against the shock direction
            imp *= .5;

        p.vel += imp; // apply collision impulse

        // handle mouse input
        if (iMouse.z > 0.)
        {
            vec2 m = (iMouse.xy - .5 * res) / res.y;
            const float MOUSE_RANGE = .05;
            if (distance(m, p.pos) < MOUSE_RANGE)
            {
                const float MOUSE_STRENGTH = .01;
                p.vel += (p.pos - m) * MOUSE_STRENGTH;
            }
        }

        // handle boundary
        float ds = sdScene(p.pos, res, s.t + toff);
        if (ds < BOUNDARY_DIST * 2.)
        {
            vec2 n = normScene(p.pos, res, s.t + toff);
            p.sd = n; // seed shock direction
            p.wv = velScene(p.pos, s.t + toff); // seed wall velocity

            if (ds < BOUNDARY_DIST) // collide with boundary
            {
                p.pos -= n * (ds - BOUNDARY_DIST);
                p.vel -= BOUNDARY_ELASTICITY * min(0., dot(p.vel, n)) * n;
                col = true;
            }            
        }

        if (col) p.pos += p.wv; // travel with local wall velocity if we collided with anything
    }

    return fxSaveParticle(p, dataType);
}

// PARTICLE NEIGHBOR UPDATE

bool iscoincidence(uvec4 bestIds, uint currentId, uint id)
{
    return id == -1U || id >= g_MaxParticles ||
      	id == currentId ||
        any(equal(bestIds,uvec4(id)));
}

void sort0(sampler2D par, uint wd, inout uvec4 bestIds, inout vec4 bestDists, uint currentId, uint searchId, uint dataType, vec2 myPos)
{
    if(iscoincidence(bestIds, currentId, searchId)) return; //particle already sorted

    vec2 nbX = fxGetParticleDataImpl(par, wd, searchId, POS).xy; 

    vec2 dx = nbX - myPos;
    uint dir = uint(2.*(atan(dx.y, dx.x)+PI)/PI); 

    if(dir != dataType) return; //not in this sector

    float t = length2(dx);

    insertionSort(bestIds, bestDists, searchId, t);
}

#define voronoiParticle(A, B, C) voronoiParticleImpl(iChannel0, iChannel1, R, iFrame, A, B, C)
vec4 voronoiParticleImpl(sampler2D par, sampler2D vor, vec2 res, int iFrame, fxParticle p, uint id, uint dataType)
{
    uvec4 closest = fxGetClosestImpl(vor, ivec2(p.pos * res.y + .5 * res));
    uvec4 bestIds = uvec4(-1);
    vec4 bestDists = vec4(1e6);

    for (int i = 0; i < 4; ++i)
    {
        sort0(par, uint(res.x), bestIds, bestDists, id, p.nbs[0][i], dataType, p.pos);
        sort0(par, uint(res.x), bestIds, bestDists, id, p.nbs[1][i], dataType, p.pos);
        sort0(par, uint(res.x), bestIds, bestDists, id, p.nbs[2][i], dataType, p.pos);
        sort0(par, uint(res.x), bestIds, bestDists, id, p.nbs[3][i], dataType, p.pos);
        sort0(par, uint(res.x), bestIds, bestDists, id, closest[i], dataType, p.pos);
    }

    uint searchIterations = 4U;
    for(uint k = 0U; k < searchIterations; k++)
    {
        //random hash. We should make sure that two pixels in the same frame never make the same hash!
        float h = hash3(uvec3(id + k, id * k, k * uint(iFrame))).x;
        //pick random id of particle
        uint s = uint(mod(h*34534.0, float(g_MaxParticles)));
        sort0(par, uint(res.x), bestIds, bestDists, id, s, dataType, p.pos);  //sort this
    }

    return uintBitsToFloat(bestIds);
}

