// ---------------------------------------------------------------------------------------
//	Voronoi tracking buffer and persistent state
// ---------------------------------------------------------------------------------------

vec4 voronoiScreen(vec2 u);
vec4 updateState(fxState s);

void mainImage(out vec4 O, vec2 u)
{
    fxState s = fxGetState();
    computeMaxParticles();

    if (fxIsStatePixel(u))
        O = updateState(s);
    else
        O = voronoiScreen(u);
}

vec4 updateState(fxState s)
{
    if (iFrame == 0 || R.x * R.y != abs(s.res) || keyDown(KEY_SPACE))
    {
        s.res = -R.x * R.y;
        s.t = 0.;
    }
    else
    {
        s.res = abs(s.res);
        s.t = fract(s.t + DT);
    }

    return fxPutState(s);
}

// find the squared distance from the screen position to a particular particle
float distance2Particle(uint id, vec2 u)
{
    fxParticle v = fxGetParticle(id);

    return min(length2(v.pos - u), length2(v.pos - v.vel - u));
}

// get all the existing neighbors of a cell and consider them as possible improvements for ourselves
void sortNbs(uvec4 old, vec2 p, inout uvec4 new, inout vec4 dis)
{
    for (int j = 0; j < 4; j++)
    {
        uint id = old[j];
        if (id == -1U || id >= g_MaxParticles) break;
        float dis2 = distance2Particle(id, p);
        insertionSort( new, dis, id, dis2 );
    }
}

vec4 voronoiScreen(vec2 u)
{
	vec2 p = (u - .5 * R.xy) / R.y;
    uvec4 new = uvec4(-1); // the four new closest particles
    vec4 dis = vec4(1e6);  // the distances of the four newest particles

    if (iFrame > 0 && !keyClick(KEY_SPACE))
    {
        uvec4 old = fxGetClosest(ivec2(u));

        sortNbs(old, p, new, dis); // start with our previous frame's values, and look up the distances

        int SEARCH_STEPS = int(2e4 / R.y);
        const float SEARCH_RANGE = 20.;

        for(int i = 0; i < SEARCH_STEPS; ++i)
        {
            vec2 h = hash3(uvec3(u, iFrame * 4 + i)).xy - .5;

            sortNbs(fxGetClosest(ivec2(u + vec2(h * SEARCH_RANGE))), p, new, dis);
        }

        // random searching to kick start the process
        float h = hash3(uvec3(u, iFrame)).x;
        uint id = uint(h*float(g_MaxParticles));
        insertionSort(new, dis, id, distance2Particle(id, p));
    }

    return uintBitsToFloat(new);
}

