//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

//https://www.shadertoy.com/view/mtyGWy
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.);

    for (float i = 0.0; i < 4.0; i++) {
        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*iMouse.x + iTime*.4);

        d = sin(d*8. + iTime)/8.;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}

// Public Domain under http://unlicense.org, see link for details.

// Couple of functions related to scotopic vision
// (see https://en.wikipedia.org/wiki/Scotopic_vision).
// Unlike color matching functions for photopic vision
// (e.g. https://www.shadertoy.com/view/msXyDH),
// there doesn't seem to be many readily available
// on Shadertoy, as of this writing.

// The provided functions are:
//   wavelength2scotopic - for when you have spectral data.
//   xyz2scotopic - for when you don't, and have to make do with XYZ/RGB.

// WARNING: the actual displayed image is highly fake, due to liberties
// taken. The functions themselves should be decent enough, though.

//==============================================================================
// RGB<->XYZ conversions, from https://en.wikipedia.org/wiki/SRGB
// Operate on linear RGB.

vec3 xyz2rgb(vec3 xyz)
{
    return mat3( // WARNING: column-major.
        +3.2404542, -0.9692660, +0.0556434,
        -1.5371385, +1.8760108, -0.2040259,
        -0.4985314, +0.0415560, +1.0572252)*xyz;
}

vec3 rgb2xyz(vec3 rgb)
{
    return mat3( // WARNING: column-major.
        0.4124, 0.2126, 0.0193,
        0.3576, 0.7152, 0.1192,
        0.1805, 0.0722, 0.9505)*rgb;
}

//==============================================================================
// sRGB<->linear conversions, from https://en.wikipedia.org/wiki/SRGB
// NOTE: in floating point (1.055-1.0)!=0.055, but whatever.

vec3 linear2srgb(vec3 rgb)
{
    return mix(12.92*rgb,1.055*pow(rgb,vec3(1.0/2.4))-0.055,step(0.0031308,rgb));
}

vec3 srgb2linear(vec3 rgb)
{
    return mix(rgb/12.92,pow((rgb+0.055)/1.055,vec3(2.4)),step(0.04045,rgb));
}

//==============================================================================
// Approximation of scotopic luminous efficiency function,
// CIE (1951) Scotopic V'(lambda) (see http://www.cvrl.org/lumindex.htm)
// Input is in nanometers.
// Max. abs. error: ~0.043.

float wavelength2scotopic(float lambda)
{
    float t=lambda-507.0;
    //t*=mix(0.01692040668,0.01919261909,step(0.0,t)); // <-- old version, error ~0.061.
    t*=mix(0.01650,0.01919,step(0.0,t));
    return exp(-t*t);
}

//==============================================================================
// Scotopic luminous flux approximation from XYZ,
// see http://integral.physics.muni.cz/rawtran/.

float xyz2scotopic(vec3 xyz) {return max(0.0,dot(xyz,vec3(-0.80498,1.18214,0.36169)));}

//==============================================================================

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fragCoord/iResolution.xy;
    vec3 rgb=texture(iChannel0,uv).rgb;

    rgb=srgb2linear(rgb); // Do we need this?
    vec3 xyz=rgb2xyz(rgb);
    float v=xyz2scotopic(xyz);
    float a=0.25*iTime;
    // Mesopic weighting factor. In reality it depends
    // on luminance (possibly both global and local) of the scene.
    float t=smoothstep(0.0,1.0,0.5+dot(2.0*uv-1.0,vec2(cos(a),sin(a))));
    // NOTE: the "night color" below, assigned to
    // the scotopic component is chosen pretty arbitrarily.
    // Charitably put, it tries to approximate
    // the Purkinje effect (https://en.wikipedia.org/wiki/Purkinje_effect),
    // but really, it is just a random fake, chosen to look okay-ish.
    // Better designed models of Purkinje effect do exist, though.
    // This also doesn't deal with the *rest* of the
    // tone-mapping, at all.
    vec3 night=vec3(0.375,0.75,1.0);
    rgb=mix(v*night,rgb,t);
    rgb=linear2srgb(rgb); // Shadertoy seems to treat output as sRGB.
    if(false) // Render plot.
    {
        float lambda=mix(350.0,750.0,uv.x);
        float d=wavelength2scotopic(lambda)-2.0*uv.y;
        rgb+=exp(-1e5*d*d);
    }
    fragColor=vec4(rgb,1.0);
}

#define PI 3.14159265359

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

vec3 palette( in float t )
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(2.0, 1.0, 1.0);
    vec3 d = vec3(0.50, 0.20, 0.25);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 1.-fract(Hash21(vec2(1., 2.5)));

    vec2 gv = uv;
    gv *= fract(gv)*.3;

    float t = iTime*.05*(Hash21(vec2(.3, 2.7)));
    gv *= Rot(t);

    float d = length(uv) * .5-length(uv); // change colour and pattern
    d = length(uv) * 1.-fract(abs(gv.x * gv.y*450.));
    vec3 col = palette(d);

    float rays = fract(gv.x * gv.y*150.);
    rays = 1.*cos(abs(gv.x * gv.y*250.));
    col += smoothstep(.2, .8, rays);

    // Time varying pixel color
    col += 0.25 + 1.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define ROTATING_EDGE 0
#define SIDEWAYS      1

#define VERSION ROTATING_EDGE

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 normalizedUv = (fragCoord / iResolution.xy);
    vec2 centeredUv   = normalizedUv * 2.0 - 1.0;
    centeredUv.x     *= iResolution.x / iResolution.y;

    float sphereDist = abs(dot(centeredUv, centeredUv) - 0.5);
#if VERSION == ROTATING_EDGE
    sphereDist      += abs(centeredUv.x + sin(iTime) * 0.7); // Comment to get a horizontal line going up & down
    sphereDist      += abs(centeredUv.y + cos(iTime) * 0.7); // Comment to get a vertical line going sideways
    // Comment both to get a glowing circle
#elif VERSION == SIDEWAYS
    sphereDist      += (sin(centeredUv.x) * cos(centeredUv.y) * sin(iTime)) * 0.75 + 0.5;
#endif
    sphereDist       = 0.05 / sphereDist; // Fake bloom
#if VERSION == ROTATING_EDGE
    sphereDist       = log(sphereDist) * 0.15 + 0.5; // Smoothing the image's lighting
#endif

    vec3 color = vec3(sphereDist);

    fragColor = vec4(color, 1.0);
}

#define scale 5.

void mainImage(out vec4 O, in vec2 u)
{
    u = u/iResolution.y;
    O -= O;

    u *= scale;

    vec2 grid = mod(floor(u),2.);
    u.x += (smoothstep(0.,1.,min(mod(iTime,6.),1.)) + smoothstep(0.,1.,max(min(mod(iTime,6.)-3.,1.),0.))) *((grid.y * 2.) - 1.);
    u.y += (smoothstep(0.,1.,max(min(mod(iTime,6.)-1.5,1.),0.)) + smoothstep(0.,1.,max(min(mod(iTime,6.)-4.5,1.),0.))) *((grid.x * 2.) - 1.);

    vec2 suv = (fract(abs(u))-.5)*2.;

    float o = abs(length(suv)-.5);
    o = smoothstep(.05, 0., o) + smoothstep(.15, -.15, o);

    float x = distance(abs(suv.x), abs(suv.y));    
    x = smoothstep(.06, 0., x) + smoothstep(.15, -.15, x);
    x *= smoothstep(0.,.2,clamp(1.-length(suv),0.,1.));

    vec2 guv= abs(mod(floor(u), 2.));
    float cb = mod(guv.x+guv.y,2.);    

    vec4 col = cos(iTime + vec4(0.,1.,2.,0.)) + 1.;

    O = mix(o,x,cb) * col * 2.;
}

// MitSync, 2023

#define M_PI 3.1415926535897932384626433832795

#define MARCH_THRESH 0.0001
#define ITERATIONS 250
#define REFLECTIONS 3
#define FOV 0.8
#define NORMAL_H 0.05

const vec3 camera_pos = vec3(2.5, 0.0, 0.0);
const vec3 camera_dir = vec3(-1.0, 0.0, 0.0);
const vec4 amb_light = vec4(0.500,0.500,0.500,1.0);
const vec4 sky_color = vec4(0.639,0.682,1.000,1.0);

// Planes are defined in a vec4 as the coefficients abcd in (a*x + b*y + c*z - d = DISTANCE)
const vec4[] planes = vec4[]                       (vec4(1.0, 0.0, 0.0, -3.0), vec4(1.0, 0.0, 0.0, 3.0));
// Spheres are defined in a vec4 as their center position and radius
const vec4[] spheres = vec4[]                      (vec4(0.0, 0.0, 0.0, 1.00), vec4(0.0, 1.0, 1.0, 0.5), vec4(0.0, -1.0, 1.0, 0.5),
                                                    vec4(-1.0, -1.0, -1.0, 0.5), vec4(1.0, 1.0, -1.0, 0.5));

// Colors are defined per primitive (same index) as a vec4 where xyz = albedo and w = specular
const vec4[] planes_color = vec4[planes.length()]  (vec4(1.000,1.000,1.000,1.0), vec4(1.000,1.000,1.000,1.0));
const vec4[] spheres_color = vec4[spheres.length()](vec4(0.5, 0.5, 0.5, 1.0), vec4(0.5, 0.5, 0.5, 1.0), vec4(0.5, 0.5, 0.5, 1.0),
                                                    vec4(0.5, 0.5, 0.5, 1.0), vec4(0.5, 0.5, 0.5, 1.0));

struct MarchReturn {
    vec3 pos;
    vec3 norm;
    vec3 albedo;
    float specular;
};

vec4 col4(in float f) {
    return vec4(f, f, f, 1.0);
}

float sdf_sphere(in vec3 pos, in vec4 sphere) {
    return length(pos-sphere.xyz)-sphere.w;
}
float sdf_plane(in vec3 pos, in vec4 plane) {
    return abs(dot(pos, plane.xyz)-plane.w);
}
// SDF is generated from lists of primitives
float sdf(in vec3 pos) {
    float d = 9999.;
    // Loop over all spheres in scene
    for (int i = 0; i < spheres.length(); i++) {
        d = min(d, sdf_sphere(pos, spheres[i]));
    }
    // Loop over all planes in scene
    for (int i = 0; i < planes.length(); i++) {
        d = min(d, sdf_plane(pos, planes[i]));
    }
    return d;
}

// Normal function of scene, computed by derivative of SDF
vec3 normal(in vec3 pos) {
    vec3 norm;
    norm.x = sdf(pos + vec3(NORMAL_H, 0.0, 0.0));
    norm.y = sdf(pos + vec3(0.0, NORMAL_H, 0.0));
    norm.z = sdf(pos + vec3(0.0, 0.0, NORMAL_H));
    return normalize(norm);
}

// Color function of scene, alpha is shininess
vec4 color(in vec3 pos) {
    // If we ask the color of air, return a hot magenta to indicate something went wrong
    if (sdf(pos) > MARCH_THRESH) return vec4(1.0, 0.0, 1.0, 0.0);

    for (int i = 0; i < spheres.length(); i++) {
        if (sdf_sphere(pos, spheres[i]) <= MARCH_THRESH) return spheres_color[i];
    }
    // Loop over all planes in scene
    for (int i = 0; i < planes.length(); i++) {
        if (sdf_plane(pos, planes[i]) <= MARCH_THRESH) return planes_color[i];
    }

    //vec4 col = pos.y > 0. ? vec4(0.5, 0.5, 0.0, 1.0) : vec4(0.0, 0.5, 0.5, 1.0);
    //vec4 col = vec4(abs(pos/2.), 1.0);
    vec4 col = vec4(normal(pos), 1.0);
    //vec4 col = vec4(0.5, 0.5, 0.5, 0.3);

    // Everything further than the camera is a shiny checkerboarded mirror
    //vec4 mirror = vec4(vec3(mod(floor(pos.y)+floor(pos.z), 2.0) == 1. ? 1.0 : 0.5), 1.0);
    vec4 mirror = vec2(0.5, 1.0).xxxy;
    return (abs(pos.x) > camera_pos.x) ? mirror : col;
}

vec4 raycast(in vec3 pos, in vec3 dir) {
    // Total distance of ray, set to 0.0 if we didn't hit anything.
    float total_distance = 0.0;
    float d = 0.0;
    for (int j = 0; j < ITERATIONS; j++) {
        d = sdf(pos);
        // If we're shooting off
        if (d > 100.) {
            total_distance = 0.0;
            break;
        }
        // If we've hit something, we're done
        if (d < MARCH_THRESH) break;
        // March one step forward
        pos += dir * d;
        total_distance += d;
    }
    return vec4(pos, total_distance);
}

vec4 march(in vec3 pos, in vec3 dir) {
    // List of hits and how many there are (always at most REFLECTIONS)
    vec4[REFLECTIONS] hit_col_list;
    float[REFLECTIONS] hit_angle_list;
    int hits = 0;

    float tot_d = 0.0;

    // Reused variable
    vec4 raycast_results;

    for (int i = 0; i < REFLECTIONS; i++) {
        raycast_results = raycast(pos, dir);
        // If we didn't hit anything, no more reflections
        if (raycast_results.w == 0.0) break;
        tot_d += raycast_results.w;

        // Store color, we use this later to calculate final color backwards
        hit_col_list[i] = color(raycast_results.xyz);
        hit_angle_list[i] = -dot(normal(raycast_results.xyz), dir);
        hits++;
        // If the object we hit isn't reflective, no more reflections
        if (hit_col_list[i].w < 0.01) break;

        // Reflect and march a tiny bit forward because otherwise we get stuck
        pos = raycast_results.xyz;
        dir = reflect(dir, normal(pos));
        pos += dir * 0.001;
    }

    // Calculate color by following the reflections backwards
    vec4 ray_color = sky_color;
    for (int i = hits-1; i >= 0; i--) {
        ray_color = (amb_light * hit_col_list[i] * hit_angle_list[i]) + (ray_color * hit_col_list[i] * hit_col_list[i].w);
    }

    //ray_color = color(pos);
    //return col4(tot_d/20.);
    return vec4(ray_color.xyz, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates ((0,0) is center, y goes from -1 to 1, aspect ratio preserved)
    vec2 uv = ((2.0 * fragCoord)-iResolution.xy) / iResolution.y;
    vec2 mouse_uv = ((2.0 * iMouse.xy)-iResolution.xy) / iResolution.y;

    // Calculate direction of ray through this pixel (not accurate but good enough for now)
    vec3 dir = camera_dir + vec3(0.0, FOV*(uv));

    // March the ray and output returned color
    fragColor = march(camera_pos - 2.*vec3(0.0, mouse_uv), normalize(dir));
}

